 m_User = user;
 ActionBar actionBar = getSupportActionBar();

 // Cp nht tiu ActionBar vi tn ngi dng
 if (actionBar != null) {
 actionBar.setTitle("Welcome, " + m_User.getFirstName() + " " + m_User.getLastName());
 }
 }

 @Override
 public void onFailure(DatabaseError error) {
