```java:app/src/main/java/com/example/argapp/Activities/SplashActivity.java
package com.example.argapp.Activities;

import android.content.Intent;
import android.os.Bundle;
import android.os.Handler;

import androidx.activity.EdgeToEdge;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;

import com.example.argapp.R;

/**
 * Mn hnh khi ng (Splash Screen) ca ng dng
 * Hin th trong mt khong thi gian ngn trc khi chuyn sang MainActivity
 */
public class SplashActivity extends AppCompatActivity {

 // Thi gian hin th mn hnh Splash (tnh bng mili giy)
 private static final int SPLASH_TIMEOUT = 2000; // 2 giy

 @Override
 protected void onCreate(Bundle savedInstanceState) {
 super.onCreate(savedInstanceState); // Gi phng thc ca lp cha
 EdgeToEdge.enable(this); // Kch hot ch hin th Edge-to-Edge cho giao din p hn
 setContentView(R.layout.activity_splash); // Thit lp layout cho activity

 // Thit lp listener x l window insets cho vic hin th edge-to-edge
 ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main), (v, insets) -> {
 // Ly insets cho cc thanh h thng (status bar, navigation bar)
 Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
 // Thit lp padding cho view da trn cc insets trnh ni dung b che khut
 v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
 return insets;
 });

 // To mt Handler tr hon vic chuyn sang MainActivity
 new Handler().postDelayed(new Runnable() {
 @Override
 public void run() {
 // To v khi chy Intent chuyn n MainActivity
 startActivity(new Intent(SplashActivity.this, MainActivity.class));
 finish(); // ng SplashActivity ngi dng khng th quay li bng nt Back
 }
 }, SPLASH_TIMEOUT); // Tr hon trong khong thi gian SPLASH_TIMEOUT (2 giy)
 }
}
