package com.example.argapp.Adapters;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.example.argapp.Activities.MainActivity;
import com.example.argapp.Classes.Category;
import com.example.argapp.Interfaces.OnCategoryClickListener;
import com.example.argapp.R;

import java.util.List;

/**
 * Adapter cho RecyclerView hin th danh sch cc danh mc
 * Kt ni d liu danh mc vi giao din ngi dng
 */
public class CategoriesAdapter extends RecyclerView.Adapter<CategoriesAdapter.ViewHolder> {
 private List<Category> m_CategoriesList; // Danh sch cc i tng Category hin th
 private OnCategoryClickListener m_Listener; // Interface callback x l s kin click
 private MainActivity m_HostedActivity; // Activity cha RecyclerView truy cp resources

 /**
 * Constructor cho CategoriesAdapter
 * @param i_HostedActivity Activity cha RecyclerView
 * @param i_CategoriesList Danh sch cc i tng Category
 * @param i_Listener Listener x l s kin click trn danh mc
 */
 public CategoriesAdapter(MainActivity i_HostedActivity, List<Category> i_CategoriesList, OnCategoryClickListener i_Listener)
 {
 this.m_HostedActivity = i_HostedActivity;
 this.m_CategoriesList = i_CategoriesList;
 this.m_Listener = i_Listener;
 }

 /**
 * To ViewHolder mi bng cch inflate layout cho item danh mc
 * @param parent ViewGroup cha cha ViewHolder mi
 * @param viewType Loi view (khng s dng trong trng hp ny)
 * @return ViewHolder mi c khi to
 */
 @NonNull
 @Override
 public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
 View view = LayoutInflater.from(parent.getContext()).inflate(R.layout.category_item, parent, false);
 return new ViewHolder(view, m_Listener);
 }

 /**
 * Gn d liu t danh sch vo ViewHolder ti v tr ch nh
 * @param holder ViewHolder c gn d liu
 * @param position V tr ca item trong danh sch
 */
 @Override
 public void onBindViewHolder(@NonNull ViewHolder holder, int position) {
 Category category = m_CategoriesList.get(position); // Ly i tng Category v tr position

 // Ly ID hnh nh t tn file trong resources
 int categoryImage = m_HostedActivity.getResources().getIdentifier(
 category.getImage(), "drawable", m_HostedActivity.getPackageName());

 // Thit lp hnh nh v tn danh mc cho ViewHolder
 holder.m_CategoryImage.setImageResource(categoryImage);
 holder.m_CategoryName.setText(category.getName());

 // Thit lp s kin click cho item
 holder.itemView.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 if(m_Listener != null)
 {
 m_Listener.onCategoryClick(category.getId()); // Gi callback vi ID ca danh mc
 }
 }
 });
 }

 /**
 * Tr v s lng item trong danh sch
 * @return S lng danh mc
 */
 @Override
 public int getItemCount() {
 return m_CategoriesList.size();
 }

 /**
 * Lp ViewHolder gi v qun l cc view ca mi item trong RecyclerView
 */
 public static class ViewHolder extends RecyclerView.ViewHolder {
 private ImageView m_CategoryImage; // View hin th hnh nh danh mc
 private TextView m_CategoryName; // View hin th tn danh mc

 /**
 * Constructor ca ViewHolder
 * @param itemView View i din cho mt item trong danh sch
 * @param i_Listener Listener x l s kin click (khng dng trc tip trong ViewHolder)
 */
 public ViewHolder(@NonNull View itemView, OnCategoryClickListener i_Listener) {
 super(itemView);
 // nh x cc view t layout
 m_CategoryImage = itemView.findViewById(R.id.categoryImage);
 m_CategoryName = itemView.findViewById(R.id.categoryName);
 }
 }
}
