 {
 holder.m_IsAddedToCart.setImageResource(R.drawable.filled_shopping_cart); // thm vo gi
 }
 else
 {
 holder.m_IsAddedToCart.setImageResource(R.drawable.blank_shopping_cart); // Cha thm vo gi
 }

 // Thit lp s kin click cho item sn phm
 holder.itemView.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 m_Listener.onItemClick(categoryItem); // Gi callback vi i tng Item
 }
 });

 // Thit lp s kin click cho nt thm vo gi hng
 holder.m_IsAddedToCart.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 m_Listener.onAddToCartClick(categoryItem); // Gi callback thm vo gi hng
 }
 });

 // Thit lp s kin click cho nt yu thch
 holder.m_IsLiked.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 m_Listener.onLikeClick(categoryItem); // Gi callback yu thch
 }
 });
 }

 /**
 * Tr v s lng item trong danh sch
 * @return S lng sn phm
 */
 @Override
 public int getItemCount() {
 return m_CategoryItemsList.size();
