 * Kim tra xem mt sn phm c trong danh sch yu thch hay khng
 * @param i_Item i tng Item cn kim tra
 * @return true nu sn phm c yu thch, false nu cha
 */
 private boolean isItemLiked(Item i_Item)
 {
 return m_UserLikedItemsList.containsKey(i_Item.getName());
 }

 /**
 * Kim tra xem mt sn phm c trong gi hng hay khng
 * @param i_Item i tng Item cn kim tra
 * @return true nu sn phm c thm vo gi hng, false nu cha
 */
 private boolean isItemAddedToCart(Item i_Item)
 {
 return m_UserShoppingCart.getShoppingCart().containsKey(i_Item.getName());
 }
}
