 }

 /**
 * Gn d liu t danh sch vo ViewHolder ti v tr ch nh
 * @param holder ViewHolder c gn d liu
 * @param position V tr ca item trong danh sch
 */
 @Override
 public void onBindViewHolder(@NonNull ViewHolder holder, int position) {
 int itemPosition = position; // Lu li v tr hin ti s dng trong cc listener
 Item likedItem = m_UserLikedItemsList.get(position); // Ly i tng Item v tr position
 String itemImage = likedItem.getImage(); // Ly tn file hnh nh

 // Thit lp tn sn phm
 holder.m_ItemName.setText(likedItem.getName());

 // Ly ID hnh nh t tn file v thit lp cho ImageView
 holder.m_ItemImage.setImageResource(m_HostedActivity.getResources().getIdentifier(
 itemImage, "drawable", m_HostedActivity.getPackageName()));

 double price = likedItem.getPrice();

 // nh dng v hin th gi sn phm vi 2 ch s thp phn
 holder.m_ItemPrice.setText(String.format("%.2f", price));

 // Thit lp icon yu thch lun l tri tim y (v y l danh sch yu thch)
 holder.m_LikeButton.setImageResource(R.drawable.filled_heart);

 // Cp nht icon gi hng da trn trng thi hin ti
 if(isItemAddedToCart(likedItem))
 {
 holder.m_AddToCartButton.setImageResource(R.drawable.filled_shopping_cart); // thm vo gi
 }
 else
 {
 holder.m_AddToCartButton.setImageResource(R.drawable.blank_shopping_cart); // Cha thm vo gi
 }

 // Thit lp s kin click cho nt thm vo gi hng
 holder.m_AddToCartButton.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 m_Listener.onAddToCartClick(likedItem); // Gi callback thm vo gi hng
 }
 });

 // Thit lp s kin click cho nt yu thch
 holder.m_LikeButton.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 m_UserLikedItemsList.remove(itemPosition); // Xa khi danh sch yu thch
 m_Listener.onLikeClick(likedItem); // Gi callback hy yu thch
 }
 });

 // Thit lp s kin click cho ton b item
 holder.itemView.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 m_Listener.onItemClick(likedItem); // Gi callback khi click vo item
 }
 });
 }

 /**
 * Tr v s lng item trong danh sch yu thch
 * @return S lng sn phm yu thch
 */
 @Override
 public int getItemCount() {
 return m_UserLikedItemsList.size();
 }

 /**
 * Kim tra xem mt sn phm c trong gi hng hay khng
 * @param item i tng Item cn kim tra
 * @return true nu sn phm c thm vo gi hng, false nu cha
 */
 public boolean isItemAddedToCart(Item item)
 {
 return m_UserShoppingCart.getShoppingCart().containsKey(item.getName());
 }

 /**
 * Lp ViewHolder gi v qun l cc view ca mi item trong RecyclerView
 */
 public static class ViewHolder extends RecyclerView.ViewHolder {
 private TextView m_ItemName; // View hin th tn sn phm
 private ImageView m_ItemImage; // View hin th hnh nh sn phm
 private TextView m_ItemPrice; // View hin th gi sn phm
 private ImageButton m_LikeButton; // Nt yu thch/b yu thch
 private ImageButton m_AddToCartButton; // Nt thm vo gi hng

 /**
 * Constructor ca ViewHolder
 * @param itemView View i din cho mt item trong danh sch
 */
 public ViewHolder(@NonNull View itemView) {
 super(itemView);
 // nh x cc view t layout
 m_ItemName = itemView.findViewById(R.id.itemName);
 m_ItemImage = itemView.findViewById(R.id.itemImage);
 m_ItemPrice = itemView.findViewById(R.id.itemPrice);
 m_LikeButton = itemView.findViewById(R.id.likeButton);
 m_AddToCartButton = itemView.findViewById(R.id.addToCartButton);
 }
 }
}
