```java:app/src/main/java/com/example/argapp/Classes/CategoriesList.java
package com.example.argapp.Classes;
import android.content.Context;
import android.widget.Toast;

import androidx.annotation.NonNull;

import com.example.argapp.Interfaces.OnCategoriesFetchedListener;
import com.google.firebase.database.DataSnapshot;
import com.google.firebase.database.DatabaseError;
import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;
import com.google.firebase.database.ValueEventListener;

import java.util.ArrayList;
import java.util.List;

/**
 * Lp CategoriesList cung cp phng thc truy vn v ly danh sch cc danh mc t Firebase
 * S dng m hnh callback tr d liu v sau khi hon tt qu trnh truy vn bt ng b
 */
public class CategoriesList {

 // Danh sch lu tr cc i tng Category c ly t Firebase
 private static List<Category> m_CategoriesList = new ArrayList<>();

 /**
 * Phng thc ly danh sch danh mc t Firebase Realtime Database
 * @param context Context hin th thng bo li nu cn
 * @param callback Interface callback tr v kt qu sau khi truy vn thnh cng hoc tht bi
 */
 public static void GetCategoriesList(Context context, OnCategoriesFetchedListener callback)
 {
 // Khi to tham chiu n Firebase Database
 FirebaseDatabase m_Database;
 DatabaseReference m_Ref;

 // Ly instance ca Firebase Database
 m_Database = FirebaseDatabase.getInstance();
 // Ch nh ng dn n node "Data/Categories" trong database
 m_Ref = m_Database.getReference("Data/Categories");

 // Xa danh sch c trnh trng lp d liu khi ti li
 m_CategoriesList.clear();

 // Thm listener mt ln ly d liu t Firebase
 // addListenerForSingleValueEvent s ch ly d liu mt ln, khng lng nghe cc thay i
 m_Ref.addListenerForSingleValueEvent(new ValueEventListener() {
 /**
 * c gi khi d liu c c thnh cng t Firebase
 * @param snapshot Cha ton b d liu t ng dn ch nh
 */
 @Override
 public void onDataChange(@NonNull DataSnapshot snapshot) {
 // Duyt qua tt c cc con (category) trong snapshot
 for (DataSnapshot itemSnapshot : snapshot.getChildren()) {
 // Ly cc thuc tnh ca category t Firebase
 String categoryId = itemSnapshot.child("Id").getValue(String.class);
 String categoryName = itemSnapshot.child("Name").getValue(String.class);
 String categoryImage = itemSnapshot.child("Image").getValue(String.class);

 // To i tng Category mi vi d liu va ly
 Category category = new Category(categoryId, categoryName, categoryImage);
 // Thm vo danh sch cc danh mc
 m_CategoriesList.add(category);
 }

 // Gi callback khi qu trnh ly d liu hon tt thnh cng
 // v tr v danh sch c in y thng tin
 callback.onCategoriesFetched(m_CategoriesList);
 }

 /**
 * c gi khi c li xy ra trong qu trnh c d liu
 * @param error Cha thng tin v li xy ra
 */
 @Override
 public void onCancelled(@NonNull DatabaseError error) {
 // Hin th thng bo li cho ngi dng
 Toast.makeText(context, error.toString(), Toast.LENGTH_SHORT).show();
 // Gi callback vi danh sch trng thng bo vic khng ly c d liu
 callback.onCategoriesFetched(new ArrayList<>());
 }
 });
 }
}
