```java:app/src/main/java/com/example/argapp/Classes/Item.java
package com.example.argapp.Classes;

/**
 * Lp Item i din cho mt sn phm trong ng dng
 * Cha cc thng tin v sn phm nh tn, m t, gi c, hnh nh...
 */
public class Item {
 private String m_Name; // Tn ca sn phm
 private String m_Description; // M t chi tit v sn phm
 private double m_Price; // Gi ca sn phm
 private String m_Image; // ng dn/tn file hnh nh ca sn phm
 private boolean m_IsInStock; // Trng thi cn hng hay khng
 private String m_CategoryId; // ID ca danh mc cha sn phm
 private int m_Quantity; // S lng sn phm (dng trong gi hng)

 /**
 * Constructor khi to i tng Item vi y thng tin
 * @param i_Name Tn hin th ca sn phm
 * @param i_Description M t chi tit v sn phm
 * @param i_Price Gi ca sn phm
 * @param i_Image ng dn/tn file hnh nh ca sn phm
 * @param i_IsInStock Trng thi cn hng hay khng
 * @param i_CategoryId ID ca danh mc cha sn phm
 */
 public Item(String i_Name, String i_Description, double i_Price, String i_Image, boolean i_IsInStock, String i_CategoryId) {
 m_Name = i_Name; // Khi to tn sn phm
 m_Description = i_Description; // Khi to m t
 m_Price = i_Price; // Khi to gi
 m_Image = i_Image; // Khi to ng dn hnh nh
 m_IsInStock = i_IsInStock; // Khi to trng thi cn hng
 m_CategoryId = i_CategoryId; // Khi to ID danh mc
 m_Quantity = 1; // Mc nh s lng ban u l 1
 }

 /**
 * Constructor sao chp to bn sao ca mt i tng Item hin c
 * @param i_Item i tng Item cn sao chp
 */
 public Item(Item i_Item) {
 this.m_Name = i_Item.getName(); // Sao chp tn
 this.m_Description = i_Item.getDescription(); // Sao chp m t
 this.m_Price = i_Item.getPrice(); // Sao chp gi
 this.m_Image = i_Item.getImage(); // Sao chp ng dn hnh nh
 this.m_IsInStock = i_Item.getIsInStock(); // Sao chp trng thi cn hng
 this.m_CategoryId = i_Item.getCategoryId(); // Sao chp ID danh mc
 this.m_Quantity = i_Item.getQuantity(); // Sao chp s lng
 }

 /**
 * Phng thc getter ly tn ca sn phm
 * @return Tn hin th ca sn phm
 */
 public String getName() {
 return m_Name;
 }

 /**
 * Phng thc getter ly m t ca sn phm
 * @return M t chi tit v sn phm
 */
 public String getDescription() {
 return m_Description;
 }

 /**
 * Phng thc getter ly gi ca sn phm
 * @return Gi ca sn phm
 */
 public double getPrice() {
 return m_Price;
 }

 /**
 * Phng thc getter ly ng dn/tn file hnh nh ca sn phm
 * @return ng dn/tn file hnh nh
 */
 public String getImage() {
 return m_Image;
 }

 /**
 * Phng thc getter kim tra trng thi cn hng ca sn phm
 * @return true nu sn phm cn hng, false nu ht hng
 */
 public boolean getIsInStock() {
 return m_IsInStock;
 }

 /**
 * Phng thc getter ly ID ca danh mc cha sn phm
 * @return ID ca danh mc
 */
 public String getCategoryId() {
 return m_CategoryId;
 }

 /**
 * Phng thc getter ly s lng ca sn phm
 * @return S lng sn phm
 */
 public int getQuantity() {
 return m_Quantity;
 }

 /**
 * Phng thc setter thit lp s lng ca sn phm
 * @param i_Quantity S lng mi cn thit lp
 */
 public void setQuantity(int i_Quantity) {
 this.m_Quantity = i_Quantity;
 }

 /**
 * Tng s lng sn phm ln 1 n v
 */
 public void incrementQuantity() {
 m_Quantity++;
 }

 /**
 * Gim s lng sn phm xung 1 n v nu s lng hin ti ln hn 1
 * @return true nu gim thnh cng, false nu s lng l 1 v khng th gim thm
 */
 public boolean decrementQuantity() {
 if(m_Quantity > 1) {
 m_Quantity--;
 return true;
 }
 return false;
 }
}
