```java:app/src/main/java/com/example/argapp/Classes/ShoppingCart.java
package com.example.argapp.Classes;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;

/**
 * Lp ShoppingCart qun l gi hng ca ngi dng
 * Cho php thm, xa, cp nht sn phm v tnh tng tin
 */
public class ShoppingCart {
 // HashMap lu tr cc sn phm trong gi hng, vi key l tn sn phm (dng lm ID)
 private HashMap<String, Item> m_ShoppingCartList;

 /**
 * Constructor khi to mt gi hng rng
 */
 public ShoppingCart()
 {
 m_ShoppingCartList = new HashMap<>();
 }

 /**
 * Thm mt sn phm vo gi hng
 * Nu sn phm tn ti trong gi, s lng s c tng ln thay v thm mi
 * @param i_Item Sn phm cn thm vo gi hng
 */
 public void addItem(Item i_Item)
 {
 String itemName = i_Item.getName();

 // Kim tra xem sn phm c trong gi hng cha
 if(m_ShoppingCartList.containsKey(itemName))
 {
 // Nu c, tng s lng ln
 Item itemToIncrement = m_ShoppingCartList.get(itemName);
 itemToIncrement.incrementQuantity();
 }
 else
 {
 // Nu cha c, thm sn phm mi vo gi hng vi s lng mc nh l 1
 Item newItem = new Item(i_Item); // To bn sao trnh tham chiu n i tng gc
 newItem.setQuantity(1);
 m_ShoppingCartList.put(itemName, newItem);
 }
 }

 /**
 * Xa mt sn phm khi gi hng da trn tn
 * @param i_ItemName Tn ca sn phm cn xa
 * @return true nu xa thnh cng, false nu khng tm thy sn phm
 */
 public boolean removeItemByName(String i_ItemName)
 {
 if(m_ShoppingCartList.containsKey(i_ItemName))
 {
 m_ShoppingCartList.remove(i_ItemName);
 return true;
 }
 return false;
 }

 /**
 * Xa mt sn phm khi gi hng da trn i tng Item
 * @param i_Item i tng Item cn xa
 * @return true nu xa thnh cng, false nu khng tm thy sn phm
 */
 public boolean removeItem(Item i_Item)
 {
 return removeItemByName(i_Item.getName());
 }

 /**
 * Cp nht s lng ca mt sn phm trong gi hng
 * @param i_ItemName Tn ca sn phm cn cp nht
 * @param i_NewQuantity S lng mi cn thit lp
 * @return true nu cp nht thnh cng, false nu khng tm thy sn phm
 */
 public boolean updateItemQuantity(String i_ItemName, int i_NewQuantity)
 {
 if(m_ShoppingCartList.containsKey(i_ItemName))
 {
 Item itemToUpdate = m_ShoppingCartList.get(i_ItemName);
 itemToUpdate.setQuantity(i_NewQuantity);
 return true;
 }
 return false;
 }

 /**
 * Tng s lng ca mt sn phm trong gi hng ln 1
 * @param i_ItemName Tn ca sn phm cn tng s lng
 * @return true nu tng thnh cng, false nu khng tm thy sn phm
 */
 public boolean incrementItemQuantity(String i_ItemName)
 {
 if(m_ShoppingCartList.containsKey(i_ItemName))
 {
 Item itemToIncrement = m_ShoppingCartList.get(i_ItemName);
 itemToIncrement.incrementQuantity();
 return true;
 }
 return false;
 }

 /**
 * Gim s lng ca mt sn phm trong gi hng xung 1
 * Nu s lng l 1, sn phm s c xa khi gi hng
 * @param i_ItemName Tn ca sn phm cn gim s lng
 * @return true nu gim thnh cng, false nu khng tm thy sn phm
 */
 public boolean decrementItemQuantity(String i_ItemName)
 {
 if(m_ShoppingCartList.containsKey(i_ItemName))
 {
 Item itemToDecrement = m_ShoppingCartList.get(i_ItemName);
 // Nu s lng > 1, gim xung. Ngc li, xa khi gi hng
 if(!itemToDecrement.decrementQuantity())
 {
 m_ShoppingCartList.remove(i_ItemName);
 }
 return true;
 }
 return false;
 }

 /**
 * Tnh tng tin ca tt c cc sn phm trong gi hng
 * @return Tng gi tr ca gi hng
 */
 public double calculateTotal()
 {
 double total = 0;
 // Duyt qua tt c cc sn phm trong gi hng
 for(Item item : m_ShoppingCartList.values())
 {
 // Cng dn gi * s lng ca mi sn phm
 total += item.getPrice() * item.getQuantity();
 }
 return total;
 }

 /**
 * Kim tra xem gi hng c trng khng
 * @return true nu gi hng khng c sn phm no, false nu c t nht mt sn phm
 */
 public boolean isEmpty()
 {
 return m_ShoppingCartList.isEmpty();
 }

 /**
 * Xa tt c cc sn phm trong gi hng
 */
 public void clearCart()
 {
 m_ShoppingCartList.clear();
 }

 /**
 * Ly s lng cc loi sn phm khc nhau trong gi hng
 * @return S lng cc loi sn phm trong gi hng
 */
 public int getItemCount()
 {
 return m_ShoppingCartList.size();
 }

 /**
 * Chuyn i gi hng t HashMap sang List d dng x l hin th
 * @return Danh sch cc sn phm trong gi hng
 */
 public List<Item> getShoppingCartAsList()
 {
 // To mt ArrayList mi cha tt c cc gi tr t HashMap
 return new ArrayList<>(m_ShoppingCartList.values());
 }

 /**
 * Ly HashMap cha tt c cc sn phm trong gi hng
 * @return HashMap cha cc sn phm vi key l tn sn phm
 */
 public HashMap<String, Item> getShoppingCart()
 {
 return m_ShoppingCartList;
 }
}
