```java:app/src/main/java/com/example/argapp/Classes/CategoryItemsList.java
package com.example.argapp.Classes;

import android.content.Context;
import android.widget.Toast;

import androidx.annotation.NonNull;

import com.example.argapp.Interfaces.OnCategoryItemsFetchedListener;
import com.google.firebase.database.DataSnapshot;
import com.google.firebase.database.DatabaseError;
import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;
import com.google.firebase.database.ValueEventListener;

import java.util.ArrayList;
import java.util.List;

/**
 * Lp CategoryItemsList cung cp phng thc truy vn v ly danh sch cc sn phm
 * t Firebase Database, theo danh mc hoc tt c sn phm
 */
public class CategoryItemsList
{
 // Danh sch lu tr cc sn phm ca mt danh mc c th
 private static List<Item> m_CategoryItems = new ArrayList<>();

 // Danh sch lu tr tt c cc sn phm t tt c cc danh mc
 private static List<Item> m_AllItems = new ArrayList<>();

 /**
 * Phng thc tnh ly danh sch cc sn phm thuc mt danh mc c th t Firebase
 * @param i_CategoryId ID ca danh mc cn ly sn phm
 * @param context Context hin th thng bo li nu cn
 * @param callback Interface callback tr v kt qu sau khi truy vn
 */
 public static void GetItemsListByCategoryId(String i_CategoryId, Context context, OnCategoryItemsFetchedListener callback)
 {
 // Khi to tham chiu n Firebase Database
 FirebaseDatabase m_Database;
 DatabaseReference m_Ref;

 // Xa danh sch c trnh trng lp d liu khi ti li
 m_CategoryItems.clear();

 // Ly instance ca Firebase Database
 m_Database = FirebaseDatabase.getInstance();

 // Ch nh ng dn n node cha cc sn phm ca danh mc c th
 m_Ref = m_Database.getReference("Data/CategoriesItems/" + i_CategoryId);

 // Thm listener mt ln ly d liu t Firebase
 m_Ref.addListenerForSingleValueEvent(new ValueEventListener() {
 /**
 * c gi khi d liu c c thnh cng t Firebase
 * @param snapshot Cha ton b d liu t ng dn ch nh
 */
 @Override
 public void onDataChange(@NonNull DataSnapshot snapshot) {
 // Duyt qua tt c cc con (sn phm) trong snapshot
 for (DataSnapshot itemSnapshot : snapshot.getChildren()) {
 // Ly cc thuc tnh ca sn phm t Firebase
 String itemName = itemSnapshot.child("Name").getValue(String.class);
 Double itemPrice = itemSnapshot.child("Price").getValue(Double.class);
 String itemImage = itemSnapshot.child("Image").getValue(String.class);
 int itemQuantity = 0; // S lng mc nh ban u

 // To i tng Item mi vi d liu va ly
 Item item = new Item(itemName, itemPrice, itemQuantity, itemImage);
 // Thm vo danh sch cc sn phm ca danh mc
 m_CategoryItems.add(item);
 }

 // Gi callback khi qu trnh ly d liu hon tt thnh cng
 callback.onCategoryItemsFetched(m_CategoryItems);
 }

 /**
 * c gi khi c li xy ra trong qu trnh c d liu
 * @param error Cha thng tin v li xy ra
 */
 @Override
 public void onCancelled(@NonNull DatabaseError error) {
 // Hin th thng bo li cho ngi dng
 Toast.makeText(context, error.toString(), Toast.LENGTH_SHORT).show();
 // Gi callback vi danh sch trng thng bo vic khng ly c d liu
 callback.onCategoryItemsFetched(new ArrayList<>());
 }
 });
 }

 /**
 * Phng thc tnh ly danh sch tt c cc sn phm t tt c cc danh mc
 * @param context Context hin th thng bo li nu cn
 * @param callback Interface callback tr v kt qu sau khi truy vn
 */
 public static void GetAllItems(Context context, OnCategoryItemsFetchedListener callback)
 {
 // Khi to tham chiu n Firebase Database
 FirebaseDatabase m_Database;
 DatabaseReference m_Ref;

 // Xa danh sch c trnh trng lp d liu khi ti li
 m_AllItems.clear();

 // Ly instance ca Firebase Database
 m_Database = FirebaseDatabase.getInstance();

 // Ch nh ng dn n node gc cha tt c cc danh mc
 m_Ref = m_Database.getReference("Data/CategoriesItems");

 // Thm listener mt ln ly d liu t Firebase
 m_Ref.addListenerForSingleValueEvent(new ValueEventListener() {
 /**
 * c gi khi d liu c c thnh cng t Firebase
 * @param snapshot Cha ton b d liu t ng dn ch nh
 */
 @Override
 public void onDataChange(@NonNull DataSnapshot snapshot) {
 // Duyt qua tt c cc danh mc
 for (DataSnapshot categorySnapshot : snapshot.getChildren()) {
 // Duyt qua tt c cc sn phm trong mi danh mc
 for(DataSnapshot itemSnapshot : categorySnapshot.getChildren())
 {
 // Ly cc thuc tnh ca sn phm t Firebase
 String itemName = itemSnapshot.child("Name").getValue(String.class);
 Double itemPrice = itemSnapshot.child("Price").getValue(Double.class);
 String itemImage = itemSnapshot.child("Image").getValue(String.class);
 int itemQuantity = 0; // S lng mc nh ban u

 // To i tng Item mi vi d liu va ly
 Item item = new Item(itemName, itemPrice, itemQuantity, itemImage);
 // Thm vo danh sch tt c cc sn phm
 m_AllItems.add(item);
 }
 }

 // Gi callback khi qu trnh ly d liu hon tt thnh cng
 callback.onCategoryItemsFetched(m_AllItems);
 }

 /**
 * c gi khi c li xy ra trong qu trnh c d liu
 * @param error Cha thng tin v li xy ra
 */
 @Override
 public void onCancelled(@NonNull DatabaseError error) {
 // Hin th thng bo li cho ngi dng
 Toast.makeText(context, error.toString(), Toast.LENGTH_SHORT).show();
 // Gi callback vi danh sch trng thng bo vic khng ly c d liu
 callback.onCategoryItemsFetched(new ArrayList<>());
 }
 });
 }
}
