```java:app/src/main/java/com/example/argapp/Activities/LoginPage.java
package com.example.argapp.Activities;

import android.app.Activity;
import android.content.Intent;
import android.os.Bundle;
import android.text.TextUtils;
import android.util.Log;
import android.view.View;
import android.widget.Button;
import android.widget.EditText;
import android.widget.ProgressBar;
import android.widget.TextView;
import android.widget.Toast;

import androidx.activity.EdgeToEdge;
import androidx.activity.result.ActivityResultLauncher;
import androidx.activity.result.contract.ActivityResultContracts;
import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;

import com.example.argapp.R;
import com.google.android.gms.auth.api.signin.GoogleSignIn;
import com.google.android.gms.auth.api.signin.GoogleSignInAccount;
import com.google.android.gms.auth.api.signin.GoogleSignInClient;
import com.google.android.gms.auth.api.signin.GoogleSignInOptions;
import com.google.android.gms.common.SignInButton;
import com.google.android.gms.common.api.ApiException;
import com.google.android.gms.tasks.OnCompleteListener;
import com.google.android.gms.tasks.Task;
import com.google.firebase.auth.AuthCredential;
import com.google.firebase.auth.AuthResult;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.GoogleAuthProvider;

/**
 * Activity x l ng nhp ngi dng
 * H tr ng nhp bng email/mt khu v ng nhp vi Google
 */
public class LoginPage extends AppCompatActivity {

 private static final String TAG = "LoginPage"; // Tag dng cho Log
 private EditText m_EmailEditText, m_PasswordEditText; // Trng nhp email v mt khu
 private Button m_LoginButton; // Nt ng nhp
 private SignInButton m_GoogleSignInButton; // Nt ng nhp bng Google
 private TextView m_RegisterButton; // Nt ng k (di dng TextView)
 private TextView m_ForgotPasswordButton; // Nt qun mt khu (di dng TextView)
 private ProgressBar m_ProgressBar; // ProgressBar hin th khi ang x l ng nhp
 private FirebaseAuth m_Auth; // i tng FirebaseAuth x l xc thc
 private GoogleSignInClient m_GoogleSignInClient; // Client ng nhp Google

 /**
 * ActivityResultLauncher x l kt qu tr v t mn hnh ng nhp Google
 */
 private final ActivityResultLauncher<Intent> signInLauncher = registerForActivityResult(
 new ActivityResultContracts.StartActivityForResult(),
 result -> {
 // Kim tra kt qu tr v c thnh cng khng
 if (result.getResultCode() == Activity.RESULT_OK) {
 Intent data = result.getData();
 Task<GoogleSignInAccount> task = GoogleSignIn.getSignedInAccountFromIntent(data);
 try {
 // Ly ti khon Google ng nhp
 GoogleSignInAccount account = task.getResult(ApiException.class);
 Log.d(TAG, "firebaseAuthWithGoogle:" + account.getId());
 // Xc thc vi Firebase bng thng tin ng nhp t Google
 firebaseAuthWithGoogle(account.getIdToken());
 } catch (ApiException e) {
 // X l li khi ng nhp Google tht bi
 Log.w(TAG, "Google sign in failed", e);
 Toast.makeText(LoginPage.this, "Google sign in failed", Toast.LENGTH_SHORT).show();
 m_ProgressBar.setVisibility(View.GONE);
 }
 } else {
 // Ngi dng hy ng nhp
 m_ProgressBar.setVisibility(View.GONE);
 Log.d(TAG, "Sign in cancelled");
 }
 });

 @Override
 protected void onCreate(Bundle savedInstanceState) {
 super.onCreate(savedInstanceState); // Gi phng thc ca lp cha
 EdgeToEdge.enable(this); // Kch hot ch hin th Edge-to-Edge cho giao din p hn
 setContentView(R.layout.activity_login_page); // Thit lp layout cho activity

 // Thit lp listener x l window insets cho vic hin th edge-to-edge
 ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main), (v, insets) -> {
 Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
 v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
 return insets;
 });

 // nh x cc thnh phn giao din t layout
 m_EmailEditText = findViewById(R.id.emailEditText);
 m_PasswordEditText = findViewById(R.id.passwordEditText);
 m_LoginButton = findViewById(R.id.loginButton);
 m_GoogleSignInButton = findViewById(R.id.googleSignInButton);
 m_RegisterButton = findViewById(R.id.registerText);
 m_ForgotPasswordButton = findViewById(R.id.forgotPasswordText);
 m_ProgressBar = findViewById(R.id.progressBar);

 // Khi to FirebaseAuth instance
 m_Auth = FirebaseAuth.getInstance();

 // Thit lp listener cho nt ng nhp
 m_LoginButton.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 // X l ng nhp bng email/mt khu
 loginUser();
 }
 });

 // Cu hnh ng nhp Google vi OAuth client ID
 GoogleSignInOptions gso = new GoogleSignInOptions.Builder(GoogleSignInOptions.DEFAULT_SIGN_IN)
 .requestIdToken(getString(R.string.default_web_client_id)) // ID OAuth ly t Google Cloud Console
 .requestEmail() // Yu cu quyn truy cp email
 .build();

 // Khi to GoogleSignInClient vi cc ty chn cu hnh
 m_GoogleSignInClient = GoogleSignIn.getClient(this, gso);

 // Thit lp listener cho nt ng nhp Google
 m_GoogleSignInButton.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 // Hin th ProgressBar bo hiu ang x l
 m_ProgressBar.setVisibility(View.VISIBLE);
 // Bt u quy trnh ng nhp Google
 signInWithGoogle();
 }
 });

 // Thit lp listener cho nt ng k
 m_RegisterButton.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 // Chuyn qua mn hnh ng k
 startActivity(new Intent(LoginPage.this, RegisterPage.class));
 }
 });

 // Thit lp listener cho nt qun mt khu
 m_ForgotPasswordButton.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 // Chuyn qua mn hnh khi phc mt khu
 startActivity(new Intent(LoginPage.this, ResetPasswordPage.class));
 }
 });
 }

 /**
 * X l ng nhp bng email v mt khu
 */
 private void loginUser() {
 // Ly email v mt khu t EditText
 String email = m_EmailEditText.getText().toString().trim();
 String password = m_PasswordEditText.getText().toString().trim();

 // Kim tra email c trng khng
 if (TextUtils.isEmpty(email)) {
 m_EmailEditText.setError("Email is required");
 return;
 }

 // Kim tra mt khu c trng khng
 if (TextUtils.isEmpty(password)) {
 m_PasswordEditText.setError("Password is required");
 return;
 }

 // Hin th ProgressBar bo hiu ang x l
 m_ProgressBar.setVisibility(View.VISIBLE);

 // ng nhp bng email/mt khu qua Firebase Authentication
 m_Auth.signInWithEmailAndPassword(email, password)
 .addOnCompleteListener(new OnCompleteListener<AuthResult>() {
 @Override
 public void onComplete(@NonNull Task<AuthResult> task) {
 // n ProgressBar khi hon tt
 m_ProgressBar.setVisibility(View.GONE);

 if (task.isSuccessful()) {
 // ng nhp thnh cng, chuyn n MainActivity
 Toast.makeText(LoginPage.this, "Login successful", Toast.LENGTH_SHORT).show();
 startActivity(new Intent(LoginPage.this, MainActivity.class));
 finish(); // ng mn hnh ng nhp
 } else {
 // ng nhp tht bi, hin th thng bo li
 Toast.makeText(LoginPage.this, "Authentication failed: " + task.getException().getMessage(), Toast.LENGTH_SHORT).show();
 }
 }
 });
 }

 /**
 * Bt u quy trnh ng nhp bng Google
 */
 private void signInWithGoogle() {
 // To Intent ng nhp Google
 Intent signInIntent = m_GoogleSignInClient.getSignInIntent();
 // Khi chy activity ng nhp Google qua ActivityResultLauncher
 signInLauncher.launch(signInIntent);
 }

 /**
 * Xc thc vi Firebase s dng token ID ca Google
 * @param idToken Token ID t ti khon Google ng nhp
 */
 private void firebaseAuthWithGoogle(String idToken) {
 // To credentials cho Firebase t token Google
 AuthCredential credential = GoogleAuthProvider.getCredential(idToken, null);

 // ng nhp vo Firebase bng Google credential
 m_Auth.signInWithCredential(credential)
 .addOnCompleteListener(this, new OnCompleteListener<AuthResult>() {
 @Override
 public void onComplete(@NonNull Task<AuthResult> task) {
 // n ProgressBar khi hon tt
 m_ProgressBar.setVisibility(View.GONE);

 if (task.isSuccessful()) {
 // ng nhp thnh cng, chuyn n MainActivity
 Log.d(TAG, "signInWithCredential:success");
 Toast.makeText(LoginPage.this, "Login with Google successful", Toast.LENGTH_SHORT).show();
 startActivity(new Intent(LoginPage.this, MainActivity.class));
 finish(); // ng mn hnh ng nhp
 } else {
 // ng nhp tht bi, hin th thng bo li
 Log.w(TAG, "signInWithCredential:failure", task.getException());
 Toast.makeText(LoginPage.this, "Authentication failed: " + task.getException().getMessage(), Toast.LENGTH_SHORT).show();
 }
 }
 });
 }
}
