```java:app/src/main/java/com/example/argapp/Activities/MainActivity.java
package com.example.argapp.Activities;

import android.content.DialogInterface;
import android.content.Intent;
import android.os.Bundle;
import android.view.MenuItem;
import android.view.View;
import android.widget.ImageButton;
import android.widget.TextView;
import android.widget.Toast;

import androidx.activity.OnBackPressedCallback;
import androidx.annotation.NonNull;
import androidx.appcompat.app.ActionBarDrawerToggle;
import androidx.appcompat.app.AlertDialog;
import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;
import androidx.core.view.GravityCompat;
import androidx.drawerlayout.widget.DrawerLayout;
import androidx.fragment.app.Fragment;
import androidx.fragment.app.FragmentManager;

import com.example.argapp.Classes.Item;
import com.example.argapp.Classes.ShoppingCart;
import com.example.argapp.Fragments.CategoryFragment;
import com.example.argapp.Fragments.HomeFragment;
import com.example.argapp.Fragments.LikedItemsFragment;
import com.example.argapp.Fragments.ShoppingCartFragment;
import com.example.argapp.R;
import com.google.android.material.bottomnavigation.BottomNavigationView;
import com.google.android.material.navigation.NavigationBarView;
import com.google.android.material.navigation.NavigationView;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;

import java.util.HashMap;
import java.util.Objects;

/**
 * Activity chnh ca ng dng, qun l iu hng v hin th cc Fragment
 * iu phi gia cc Fragment, qun l gi hng, danh sch yu thch v trng thi ngi dng
 */
public class MainActivity extends AppCompatActivity implements NavigationView.OnNavigationItemSelectedListener{

 // Cc Fragment chnh ca ng dng
 private HomeFragment m_HomeFragment;
 private CategoryFragment m_CategoryFragment;
 private LikedItemsFragment m_LikedItemsFragment;
 private ShoppingCartFragment m_ShoppingCartFragment;

 // Qun l Fragment hin ti
 private FragmentManager m_FragmentManager;
 private Fragment m_ActiveFragment;

 // Cc thnh phn UI ca navigation
 private DrawerLayout m_DrawerLayout;
 private BottomNavigationView m_BottomNavigationView;
 private NavigationView m_NavigationView;
 private TextView m_LoginRegisterTextView;
 private Toolbar m_Toolbar;
 private ImageButton m_CartButton;

 // D liu ngi dng v h thng
 private ShoppingCart m_UserShoppingCart; // Gi hng ca ngi dng
 private HashMap<String, Item> m_UserLikedItemsList; // Danh sch sn phm yu thch
 private boolean m_IsLoggedIn; // Trng thi ng nhp
 private FirebaseAuth m_Auth; // Xc thc Firebase

 @Override
 protected void onCreate(Bundle savedInstanceState) {
 super.onCreate(savedInstanceState); // Gi phng thc ca lp cha
 setContentView(R.layout.activity_main); // Thit lp layout cho activity

 // Khi to FirebaseAuth
 m_Auth = FirebaseAuth.getInstance();

 // Kim tra trng thi ng nhp
 checkLoginStatus();

 // Khi to d liu ngi dng
 initUserData();

 // Khi to v thit lp UI
 setupUI();

 // Qun l nt Back
 handleBackButton();
 }

 /**
 * Khi to d liu ngi dng (gi hng, danh sch yu thch)
 */
 private void initUserData() {
 // Khi to gi hng
 m_UserShoppingCart = new ShoppingCart();

 // Khi to danh sch yu thch
 m_UserLikedItemsList = new HashMap<>();
 }

 /**
 * Thit lp cc thnh phn giao din ngi dng
 */
 private void setupUI() {
 // Thit lp Toolbar
 m_Toolbar = findViewById(R.id.toolbar);
 setSupportActionBar(m_Toolbar);
 Objects.requireNonNull(getSupportActionBar()).setDisplayShowTitleEnabled(false);

 // Khi to cc Fragment chnh
 m_HomeFragment = new HomeFragment();
 m_CategoryFragment = new CategoryFragment();
 m_LikedItemsFragment = new LikedItemsFragment();
 m_ShoppingCartFragment = new ShoppingCartFragment();

 // Thit lp qun l Fragment
 m_FragmentManager = getSupportFragmentManager();

 // Hin th HomeFragment u tin khi khi ng
 m_FragmentManager.beginTransaction().add(R.id.fragmentContainer, m_ShoppingCartFragment, "4").hide(m_ShoppingCartFragment).commit();
 m_FragmentManager.beginTransaction().add(R.id.fragmentContainer, m_LikedItemsFragment, "3").hide(m_LikedItemsFragment).commit();
 m_FragmentManager.beginTransaction().add(R.id.fragmentContainer, m_CategoryFragment, "2").hide(m_CategoryFragment).commit();
 m_FragmentManager.beginTransaction().add(R.id.fragmentContainer, m_HomeFragment, "1").commit();
 m_ActiveFragment = m_HomeFragment;

 // Thit lp Navigation Drawer
 setupNavigationDrawer();

 // Thit lp Bottom Navigation
 setupBottomNavigation();

 // Thit lp nt gi hng trn Toolbar
 setupCartButton();
 }

 /**
 * Thit lp Navigation Drawer (menu trt t bn tri)
 */
 private void setupNavigationDrawer() {
 m_DrawerLayout = findViewById(R.id.drawerLayout);
 m_NavigationView = findViewById(R.id.navigationView);

 // To ActionBarDrawerToggle hin th biu tng hamburger
 ActionBarDrawerToggle toggle = new ActionBarDrawerToggle(
 this, m_DrawerLayout, m_Toolbar, R.string.navigation_drawer_open, R.string.navigation_drawer_close);
 m_DrawerLayout.addDrawerListener(toggle);
 toggle.syncState();

 // t listener cho cc item trong Navigation Drawer
 m_NavigationView.setNavigationItemSelectedListener(this);

 // Ly v thit lp text ng nhp/ng k trong header ca Navigation Drawer
 View headerView = m_NavigationView.getHeaderView(0);
 m_LoginRegisterTextView = headerView.findViewById(R.id.loginRegisterTextView);

 // Cp nht text da trn trng thi ng nhp
 updateLoginRegisterText();

 // Thit lp s kin click cho text ng nhp/ng k
 m_LoginRegisterTextView.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 if (m_IsLoggedIn) {
 // Nu ng nhp, hin th hp thoi xc nhn ng xut
 showLogoutConfirmationDialog();
 } else {
 // Nu cha ng nhp, chuyn n trang ng nhp
 startActivity(new Intent(MainActivity.this, LoginPage.class));
 }
 }
 });
 }

 /**
 * Thit lp Bottom Navigation (thanh iu hng di cng)
 */
 private void setupBottomNavigation() {
 m_BottomNavigationView = findViewById(R.id.bottomNavigationView);

 // t listener cho cc item trong Bottom Navigation
 m_BottomNavigationView.setOnItemSelectedListener(new NavigationBarView.OnItemSelectedListener() {
 @Override
 public boolean onNavigationItemSelected(@NonNull MenuItem item) {
 int itemId = item.getItemId();

 // Chuyn i Fragment da trn item c chn
 if (itemId == R.id.action_home) {
 // Chuyn n Fragment Trang ch
 m_FragmentManager.beginTransaction().hide(m_ActiveFragment).show(m_HomeFragment).commit();
 m_ActiveFragment = m_HomeFragment;
 return true;
 } else if (itemId == R.id.action_category) {
 // Chuyn n Fragment Danh mc
 m_FragmentManager.beginTransaction().hide(m_ActiveFragment).show(m_CategoryFragment).commit();
 m_ActiveFragment = m_CategoryFragment;
 return true;
 } else if (itemId == R.id.action_favourites) {
 // Chuyn n Fragment Yu thch
 m_FragmentManager.beginTransaction().hide(m_ActiveFragment).show(m_LikedItemsFragment).commit();
 m_ActiveFragment = m_LikedItemsFragment;
 return true;
 } else if (itemId == R.id.action_profile) {
 // X l nt Profile - kim tra ng nhp hoc hin th thng tin
 handleProfileClick();
 return true;
 }
 return false;
 }
 });
 }

 /**
 * Thit lp nt gi hng trn Toolbar
 */
 private void setupCartButton() {
 m_CartButton = findViewById(R.id.cartButton);
 m_CartButton.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 // Chuyn n Fragment Gi hng
 m_FragmentManager.beginTransaction().hide(m_ActiveFragment).show(m_ShoppingCartFragment).commit();
 m_ActiveFragment = m_ShoppingCartFragment;
 // B chn tt c cc item trong Bottom Navigation
 m_BottomNavigationView.getMenu().findItem(R.id.placeholder).setChecked(true);
 }
 });
 }

 /**
 * X l s kin khi nt Back c nhn
 */
 private void handleBackButton() {
 getOnBackPressedDispatcher().addCallback(this, new OnBackPressedCallback(true) {
 @Override
 public void handleOnBackPressed() {
 if (m_DrawerLayout.isDrawerOpen(GravityCompat.START)) {
 // Nu drawer ang m, ng drawer
 m_DrawerLayout.closeDrawer(GravityCompat.START);
 } else if (m_ActiveFragment != m_HomeFragment) {
 // Nu khng HomeFragment, quay li HomeFragment
 m_FragmentManager.beginTransaction().hide(m_ActiveFragment).show(m_HomeFragment).commit();
 m_ActiveFragment = m_HomeFragment;
 m_BottomNavigationView.setSelectedItemId(R.id.action_home);
 } else {
 // Nu ang HomeFragment, hin th hp thoi xc nhn thot
 showExitConfirmationDialog();
 }
 }
 });
 }

 /**
 * Hin th hp thoi xc nhn thot ng dng
 */
 private void showExitConfirmationDialog() {
 new AlertDialog.Builder(this)
 .setTitle("Thot ng dng")
 .setMessage("Bn c mun thot ng dng khng?")
 .setPositiveButton("C", new DialogInterface.OnClickListener() {
 @Override
 public void onClick(DialogInterface dialog, int which) {
 finish(); // ng ng dng
 }
 })
 .setNegativeButton("Khng", null)
 .show();
 }

 /**
 * X l khi ngi dng nhn vo nt Profile trong Bottom Navigation
 */
 private void handleProfileClick() {
 if (m_IsLoggedIn) {
 // Nu ng nhp, hin th thng bo (c th m ProfileActivity nu c)
 Toast.makeText(MainActivity.this, "Profile", Toast.LENGTH_SHORT).show();
 } else {
 // Nu cha ng nhp, chuyn n trang ng nhp
 startActivity(new Intent(MainActivity.this, LoginPage.class));
 }
 }

 /**
 * Kim tra trng thi ng nhp ca ngi dng
 */
 private void checkLoginStatus() {
 FirebaseUser currentUser = m_Auth.getCurrentUser();
 m_IsLoggedIn = currentUser != null;
 }

 /**
 * Cp nht text hin th trn nt ng nhp/ng xut da vo trng thi hin ti
 */
 private void updateLoginRegisterText() {
 if (m_IsLoggedIn) {
 FirebaseUser user = m_Auth.getCurrentUser();
 if (user != null) {
 String displayName = user.getDisplayName();
 if (displayName != null && !displayName.isEmpty()) {
 m_LoginRegisterTextView.setText(displayName); // Hin th tn ngi dng nu c
 } else {
 m_LoginRegisterTextView.setText(user.getEmail()); // Hin th email nu khng c tn
 }
 }
 } else {
 m_LoginRegisterTextView.setText(R.string.login_register); // "Login / Register"
 }
 }

 /**
 * Hin th hp thoi xc nhn ng xut
 */
 private void showLogoutConfirmationDialog() {
 new AlertDialog.Builder(this)
 .setTitle("ng xut")
 .setMessage("Bn c mun ng xut khng?")
 .setPositiveButton("C", new DialogInterface.OnClickListener() {
 @Override
 public void onClick(DialogInterface dialog, int which) {
 // ng xut khi FirebaseAuth
 m_Auth.signOut();
 // Cp nht trng thi ng nhp v UI
 m_IsLoggedIn = false;
 updateLoginRegisterText();
 // ng drawer
 m_DrawerLayout.closeDrawer(GravityCompat.START);
 // Hin th thng bo
 Toast.makeText(MainActivity.this, " ng xut", Toast.LENGTH_SHORT).show();
 }
 })
 .setNegativeButton("Khng", null)
 .show();
 }

 /**
 * X l s kin khi mt item trong Navigation Drawer c chn
 */
 @Override
 public boolean onNavigationItemSelected(@NonNull MenuItem item) {
 int itemId = item.getItemId();

 // X l cc mc trong navigation drawer
 if (itemId == R.id.nav_home) {
 // Chuyn n Fragment Trang ch
 m_FragmentManager.beginTransaction().hide(m_ActiveFragment).show(m_HomeFragment).commit();
 m_ActiveFragment = m_HomeFragment;
 m_BottomNavigationView.setSelectedItemId(R.id.action_home);
 } else if (itemId == R.id.nav_categories) {
 // Chuyn n Fragment Danh mc
 m_FragmentManager.beginTransaction().hide(m_ActiveFragment).show(m_CategoryFragment).commit();
 m_ActiveFragment = m_CategoryFragment;
 m_BottomNavigationView.setSelectedItemId(R.id.action_category);
 } else if (itemId == R.id.nav_favourites) {
 // Chuyn n Fragment Yu thch
 m_FragmentManager.beginTransaction().hide(m_ActiveFragment).show(m_LikedItemsFragment).commit();
 m_ActiveFragment = m_LikedItemsFragment;
 m_BottomNavigationView.setSelectedItemId(R.id.action_favourites);
 } else if (itemId == R.id.nav_cart) {
 // Chuyn n Fragment Gi hng
 m_FragmentManager.beginTransaction().hide(m_ActiveFragment).show(m_ShoppingCartFragment).commit();
 m_ActiveFragment = m_ShoppingCartFragment;
 m_BottomNavigationView.getMenu().findItem(R.id.placeholder).setChecked(true);
 } else if (itemId == R.id.nav_chat) {
 // X l chc nng chat (v d: hin th thng bo tnh nng ang pht trin)
 Toast.makeText(this, "Tnh nng Chat ang c pht trin", Toast.LENGTH_SHORT).show();
 } else if (itemId == R.id.nav_settings) {
 // X l chc nng ci t (v d: m SettingsActivity nu c)
 Toast.makeText(this, "Tnh nng Settings ang c pht trin", Toast.LENGTH_SHORT).show();
 } else if (itemId == R.id.nav_help) {
 // X l chc nng tr gip (v d: m HelpActivity nu c)
 Toast.makeText(this, "Tnh nng Help ang c pht trin", Toast.LENGTH_SHORT).show();
 } else if (itemId == R.id.nav_about) {
 // X l chc nng gii thiu (v d: m AboutActivity nu c)
 Toast.makeText(this, "Tnh nng About ang c pht trin", Toast.LENGTH_SHORT).show();
 }

 // ng drawer sau khi x l
 m_DrawerLayout.closeDrawer(GravityCompat.START);
 return true;
 }

 /**
 * Ly i tng ShoppingCart ca ngi dng
 * @return i tng ShoppingCart
 */
 public ShoppingCart GetUserShoppingCart() {
 return m_UserShoppingCart;
 }

 /**
 * Ly danh sch sn phm yu thch ca ngi dng
 * @return HashMap cha cc sn phm yu thch
 */
 public HashMap<String, Item> GetUserLikedItemsList() {
 return m_UserLikedItemsList;
 }

 /**
 * Thm sn phm vo danh sch yu thch
 * @param i_Item Sn phm cn thm vo danh sch yu thch
 */
 public void AddItemToLikedItems(Item i_Item) {
 m_UserLikedItemsList.put(i_Item.getName(), i_Item);
 }

 /**
 * Xa sn phm khi danh sch yu thch
 * @param i_Item Sn phm cn xa khi danh sch yu thch
 */
 public void RemoveItemFromLikedItems(Item i_Item) {
 m_UserLikedItemsList.remove(i_Item.getName());
 }

 @Override
 protected void onResume() {
 super.onResume();
 // Cp nht trng thi ng nhp khi Activity c resume
 checkLoginStatus();
 updateLoginRegisterText();
 }
}
