```java:app/src/main/java/com/example/argapp/Activities/RegisterPage.java
package com.example.argapp.Activities;

import android.content.Intent;
import android.os.Bundle;
import android.text.TextUtils;
import android.util.Log;
import android.view.View;
import android.widget.AdapterView;
import android.widget.ArrayAdapter;
import android.widget.Button;
import android.widget.EditText;
import android.widget.ProgressBar;
import android.widget.Spinner;
import android.widget.TextView;
import android.widget.Toast;

import androidx.activity.EdgeToEdge;
import androidx.annotation.NonNull;
import androidx.appcompat.app.AppCompatActivity;
import androidx.core.graphics.Insets;
import androidx.core.view.ViewCompat;
import androidx.core.view.WindowInsetsCompat;

import com.example.argapp.Classes.CountryCodes;
import com.example.argapp.R;
import com.google.android.gms.tasks.OnCompleteListener;
import com.google.android.gms.tasks.Task;
import com.google.firebase.auth.AuthResult;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;
import com.google.firebase.auth.UserProfileChangeRequest;

import java.util.List;

/**
 * Activity x l ng k ti khon ngi dng mi
 * Cho php ngi dng nhp thng tin c nhn v to ti khon mi trong Firebase Authentication
 */
public class RegisterPage extends AppCompatActivity {

 private static final String TAG = "RegisterPage"; // Tag dng cho Log

 // Cc thnh phn giao din
 private EditText m_FullNameEditText; // Trng nhp h tn
 private EditText m_EmailEditText; // Trng nhp email
 private EditText m_PhoneEditText; // Trng nhp s in thoi
 private EditText m_PasswordEditText; // Trng nhp mt khu
 private EditText m_ConfirmPasswordEditText; // Trng nhp xc nhn mt khu
 private Spinner m_CountryCodeSpinner; // Spinner chn m quc gia
 private Button m_RegisterButton; // Nt ng k
 private TextView m_LoginTextView; // Nt chuyn sang trang ng nhp
 private ProgressBar m_ProgressBar; // ProgressBar hin th khi ang x l

 // Xc thc Firebase v d liu ngi dng
 private FirebaseAuth m_Auth; // i tng FirebaseAuth x l xc thc
 private String m_SelectedCountryCode; // M quc gia c chn
 private List<String> m_CountryCodes; // Danh sch cc m quc gia

 @Override
 protected void onCreate(Bundle savedInstanceState) {
 super.onCreate(savedInstanceState); // Gi phng thc ca lp cha
 EdgeToEdge.enable(this); // Kch hot ch hin th Edge-to-Edge cho giao din p hn
 setContentView(R.layout.activity_register_page); // Thit lp layout cho activity

 // Thit lp listener x l window insets cho vic hin th edge-to-edge
 ViewCompat.setOnApplyWindowInsetsListener(findViewById(R.id.main), (v, insets) -> {
 Insets systemBars = insets.getInsets(WindowInsetsCompat.Type.systemBars());
 v.setPadding(systemBars.left, systemBars.top, systemBars.right, systemBars.bottom);
 return insets;
 });

 // Khi to cc thnh phn giao din
 initializeViews();

 // Thit lp Spinner m quc gia
 setupCountryCodeSpinner();

 // Khi to FirebaseAuth
 m_Auth = FirebaseAuth.getInstance();

 // Thit lp listener cho nt ng k
 m_RegisterButton.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 // X l ng k ngi dng mi
 registerUser();
 }
 });

 // Thit lp listener cho nt chuyn sang trang ng nhp
 m_LoginTextView.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 // Quay li trang ng nhp
 startActivity(new Intent(RegisterPage.this, LoginPage.class));
 finish(); // ng trang ng k
 }
 });
 }

 /**
 * Khi to v nh x cc thnh phn giao din t layout
 */
 private void initializeViews() {
 m_FullNameEditText = findViewById(R.id.fullNameEditText);
 m_EmailEditText = findViewById(R.id.emailEditText);
 m_PhoneEditText = findViewById(R.id.phoneEditText);
 m_PasswordEditText = findViewById(R.id.passwordEditText);
 m_ConfirmPasswordEditText = findViewById(R.id.confirmPasswordEditText);
 m_CountryCodeSpinner = findViewById(R.id.countryCodeSpinner);
 m_RegisterButton = findViewById(R.id.registerButton);
 m_LoginTextView = findViewById(R.id.loginText);
 m_ProgressBar = findViewById(R.id.progressBar);
 }

 /**
 * Thit lp Spinner chn m quc gia
 */
 private void setupCountryCodeSpinner() {
 // Ly danh sch m quc gia t lp CountryCodes
 m_CountryCodes = CountryCodes.GetCountryCodes();

 // To Adapter cho Spinner vi danh sch m quc gia
 ArrayAdapter<String> adapter = new ArrayAdapter<>(this, android.R.layout.simple_spinner_item, m_CountryCodes);
 adapter.setDropDownViewResource(android.R.layout.simple_spinner_dropdown_item);
 m_CountryCodeSpinner.setAdapter(adapter);

 // Thit lp listener khi chn mt m quc gia
 m_CountryCodeSpinner.setOnItemSelectedListener(new AdapterView.OnItemSelectedListener() {
 @Override
 public void onItemSelected(AdapterView<?> parent, View view, int position, long id) {
 m_SelectedCountryCode = m_CountryCodes.get(position);
 }

 @Override
 public void onNothingSelected(AdapterView<?> parent) {
 // Nu khng c g c chn, mc nh l m u tin
 m_SelectedCountryCode = m_CountryCodes.get(0);
 }
 });
 }

 /**
 * X l ng k ngi dng mi
 * Kim tra d liu nhp vo v to ti khon Firebase
 */
 private void registerUser() {
 // Ly d liu t cc trng nhp liu
 String fullName = m_FullNameEditText.getText().toString().trim();
 String email = m_EmailEditText.getText().toString().trim();
 String phoneNumber = m_PhoneEditText.getText().toString().trim();
 String password = m_PasswordEditText.getText().toString().trim();
 String confirmPassword = m_ConfirmPasswordEditText.getText().toString().trim();

 // Kim tra h tn c trng khng
 if (TextUtils.isEmpty(fullName)) {
 m_FullNameEditText.setError("Full name is required");
 return;
 }

 // Kim tra email c trng khng
 if (TextUtils.isEmpty(email)) {
 m_EmailEditText.setError("Email is required");
 return;
 }

 // Kim tra s in thoi c trng khng
 if (TextUtils.isEmpty(phoneNumber)) {
 m_PhoneEditText.setError("Phone number is required");
 return;
 }

 // Kim tra mt khu c trng khng
 if (TextUtils.isEmpty(password)) {
 m_PasswordEditText.setError("Password is required");
 return;
 }

 // Kim tra di mt khu (t nht 6 k t)
 if (password.length() < 6) {
 m_PasswordEditText.setError("Password must be at least 6 characters");
 return;
 }

 // Kim tra xc nhn mt khu c trng khng
 if (TextUtils.isEmpty(confirmPassword)) {
 m_ConfirmPasswordEditText.setError("Confirm password is required");
 return;
 }

 // Kim tra xc nhn mt khu c khp khng
 if (!password.equals(confirmPassword)) {
 m_ConfirmPasswordEditText.setError("Passwords do not match");
 return;
 }

 // Hin th ProgressBar khi bt u qu trnh ng k
 m_ProgressBar.setVisibility(View.VISIBLE);

 // To s in thoi y vi m quc gia
 String fullPhoneNumber = m_SelectedCountryCode + phoneNumber;

 // To ti khon mi vi Firebase Authentication
 m_Auth.createUserWithEmailAndPassword(email, password)
 .addOnCompleteListener(new OnCompleteListener<AuthResult>() {
 @Override
 public void onComplete(@NonNull Task<AuthResult> task) {
 // n ProgressBar khi qu trnh hon tt
 m_ProgressBar.setVisibility(View.GONE);

 if (task.isSuccessful()) {
 // ng k thnh cng
 FirebaseUser user = m_Auth.getCurrentUser();
 if (user != null) {
 // Cp nht thng tin h s ngi dng vi tn y 
 UserProfileChangeRequest profileUpdates = new UserProfileChangeRequest.Builder()
 .setDisplayName(fullName)
 .build();

 user.updateProfile(profileUpdates)
 .addOnCompleteListener(new OnCompleteListener<Void>() {
 @Override
 public void onComplete(@NonNull Task<Void> task) {
 if (task.isSuccessful()) {
 Log.d(TAG, "User profile updated.");
 }
 }
 });

 // Gi email xc thc
 user.sendEmailVerification()
 .addOnCompleteListener(new OnCompleteListener<Void>() {
 @Override
 public void onComplete(@NonNull Task<Void> task) {
 if (task.isSuccessful()) {
 // Thng bo gi email xc thc thnh cng
 Toast.makeText(RegisterPage.this,
 "Registration successful. Please check your email for verification.",
 Toast.LENGTH_LONG).show();
 }
 }
 });
 }

 // Chuyn n trang ng nhp sau khi ng k thnh cng
 Intent intent = new Intent(RegisterPage.this, LoginPage.class);
 startActivity(intent);
 finish(); // ng trang ng k
 } else {
 // ng k tht bi, hin th thng bo li
 Toast.makeText(RegisterPage.this, "Registration failed: " + task.getException().getMessage(),
 Toast.LENGTH_SHORT).show();
 }
 }
 });
 }
}
