 // Lu tham chiu n Activity cha Fragment
 m_HostedActivity = (MainActivity) getActivity();

 // Khi to v ly d liu t Activity
 initialize(view);

 // Ti danh sch danh mc t Firebase
 fetchCategories();

 return view;
 }

 /**
 * Khi to cc thnh phn giao din v d liu
 * @param view View chnh ca Fragment
 */
 private void initialize(View view) {
 // nh x cc thnh phn giao din
