 m_CategoryItemsRecyclerView = view.findViewById(R.id.categoryItemsRecyclerView);
 m_ProgressBar = view.findViewById(R.id.progressBar);
 m_CategoryNameTextView = view.findViewById(R.id.categoryNameTextView);
 m_CategoryItemsLayout = view.findViewById(R.id.categoryItemsLayout);
 m_BackToCategories = view.findViewById(R.id.backButton);

 // Ly gi hng v danh sch sn phm yu thch t MainActivity
 if (m_HostedActivity != null) {
