 // n danh sch sn phm v hin th danh sch danh mc
 m_CategoryItemsLayout.setVisibility(View.GONE);
 m_CategoriesRecyclerView.setVisibility(View.VISIBLE);
 }
 });
 }

 /**
 * Ti danh sch danh mc t Firebase
 */
 private void fetchCategories() {
