 // Lu danh sch danh mc
 m_CategoriesList = categoriesList;

 // Thit lp Adapter cho RecyclerView danh mc
 CategoriesAdapter adapter = new CategoriesAdapter(m_HostedActivity, m_CategoriesList, CategoryFragment.this);
 m_CategoriesRecyclerView.setAdapter(adapter);

 // n ProgressBar khi ti d liu xong
 m_ProgressBar.setVisibility(View.GONE);
 }
 });
 }

 /**
