 // Tm tn danh mc t ID
 for (Category category : m_CategoriesList) {
 if (category.getId().equals(categoryId)) {
 m_SelectedCategoryName = category.getName();
 break;
 }
 }

 // Cp nht TextView hin th tn danh mc
 m_CategoryNameTextView.setText(m_SelectedCategoryName);

 // Gi phng thc tnh ly danh sch sn phm ca danh mc
 com.example.argapp.Classes.CategoryItems.GetCategoryItems(getContext(), categoryId, new OnCategoryItemsFetchedListener() {
 @Override
 public void onCategoryItemsFetched(List<Item> itemsList) {
 // Lu danh sch sn phm
 m_CategoryItems = itemsList;

 // Thit lp Adapter cho RecyclerView sn phm
 CategoryItemsAdapter adapter = new CategoryItemsAdapter(m_HostedActivity, m_CategoryItems, m_UserShoppingCart, m_UserLikedItemsList, CategoryFragment.this);
 m_CategoryItemsRecyclerView.setAdapter(adapter);

 // n ProgressBar khi ti d liu xong
 m_ProgressBar.setVisibility(View.GONE);
 }
 });
 }

