# Chi tit gii thch code UserModel

```java:app/src/main/java/com/example/argapp/Models/UserModel.java
package com.example.argapp.Models;

import android.app.Activity;
import android.util.Log;

import androidx.annotation.NonNull;

import com.example.argapp.Classes.Item;
import com.example.argapp.Classes.ShoppingCart;
import com.example.argapp.Classes.User;
import com.google.android.gms.tasks.OnCompleteListener;
import com.google.android.gms.tasks.Task;
import com.google.firebase.auth.AuthResult;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;
import com.google.firebase.database.DataSnapshot;
import com.google.firebase.database.DatabaseError;
import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;
import com.google.firebase.database.ValueEventListener;

import java.util.HashMap;
import java.util.Objects;

/**
 * Lp UserModel x l tt c cc tng tc vi Firebase Authentication v Realtime Database
 * lin quan n ngi dng, bao gm xc thc v lu tr d liu ngi dng
 */
public class UserModel {
 private final String TAG = "UserModel"; // Tag dng cho cc log

 // Cc i tng Firebase
 private FirebaseAuth m_Auth; // i tng x l xc thc Firebase
 private FirebaseUser m_User; // Ngi dng hin ti ng nhp
 private FirebaseDatabase m_Database; // C s d liu Firebase
 private DatabaseReference m_Ref; // Tham chiu n node trong c s d liu

 /**
 * Constructor khi to UserModel vi kt ni n Firebase
 */
 public UserModel()
 {
 // Khi to i tng xc thc Firebase
 m_Auth = FirebaseAuth.getInstance();
 // Ly thng tin ngi dng hin ti nu ng nhp
 m_User = m_Auth.getCurrentUser();
 // Khi to i tng Firebase Database
 m_Database = FirebaseDatabase.getInstance();
 // To tham chiu n node "Users" trong c s d liu
 m_Ref = m_Database.getReference("Users");
 }

 /**
 * Interface callback x l kt qu xc thc (ng nhp/ng k)
 */
 public interface AuthCallback
 {
 void onSuccess(FirebaseUser user); // Gi khi xc thc thnh cng
 void onFailure(String errorMessage); // Gi khi xc thc tht bi
 }

 /**
 * Interface callback x l vic ly thng tin ngi dng
 */
 public interface UserCallback
 {
 void onUserFetched(User user); // Gi khi ly thng tin ngi dng thnh cng
 void onFailure(); // Gi khi khng th ly thng tin ngi dng
 }

 /**
 * Interface callback x l vic cp nht gi hng
 */
 public interface UpdateShoppingCartCallback
 {
 void onSuccess(); // Gi khi cp nht gi hng thnh cng
 void onFailure(String errorMessage); // Gi khi cp nht gi hng tht bi
 }

 /**
 * Interface callback x l vic cp nht danh sch sn phm yu thch
 */
 public interface UpdateLikedItemsListCallback
 {
 void onSuccess(); // Gi khi cp nht danh sch yu thch thnh cng
 void onFailure(String errorMessage); // Gi khi cp nht danh sch yu thch tht bi
 }

 /**
 * Interface callback x l vic ly gi hng
 */
 public interface ShoppingCartCallback
 {
 void onShoppingCartFetched(ShoppingCart shoppingCart); // Gi khi ly gi hng thnh cng
 void onFailure(); // Gi khi khng th ly gi hng
 }

 /**
 * Interface callback x l vic ly danh sch sn phm yu thch
 */
 public interface LikedItemsCallback
 {
 void onLikedItemsFetched(HashMap<String, Item> likedItems); // Gi khi ly danh sch yu thch thnh cng
 void onFailure(); // Gi khi khng th ly danh sch yu thch
 }

 /**
 * ng nhp ngi dng vi email v mt khu
 * @param i_Email Email ngi dng
 * @param i_Password Mt khu ngi dng
 * @param callback Interface callback phn hi kt qu
 */
 public void Login(String i_Email, String i_Password, AuthCallback callback)
 {
 // S dng Firebase Authentication ng nhp
 m_Auth.signInWithEmailAndPassword(i_Email, i_Password)
 .addOnCompleteListener(new OnCompleteListener<AuthResult>() {
 @Override
 public void onComplete(@NonNull Task<AuthResult> task) {
 if(task.isSuccessful())
 {
 // Nu ng nhp thnh cng, ly thng tin ngi dng
 m_User = m_Auth.getCurrentUser();
 // Gi callback thnh cng vi thng tin ngi dng
 callback.onSuccess(m_User);
 }
 else
 {
 // Nu ng nhp tht bi, gi callback tht bi vi thng bo li
 callback.onFailure(Objects.requireNonNull(task.getException()).getMessage());
 }
 }
 });
 }

 /**
 * ng k ngi dng mi vi thng tin t i tng User
 * @param i_NewUser i tng User cha thng tin ngi dng mi
 * @param callback Interface callback phn hi kt qu
 */
 public void Register(User i_NewUser, AuthCallback callback)
 {
 // S dng Firebase Authentication to ngi dng mi
 m_Auth.createUserWithEmailAndPassword(i_NewUser.getEmail(), i_NewUser.getPassword())
 .addOnCompleteListener(new OnCompleteListener<AuthResult>() {
 @Override
 public void onComplete(@NonNull Task<AuthResult> task) {
 if(task.isSuccessful())
 {
 // Nu ng k thnh cng, ly thng tin ngi dng mi
 m_User = m_Auth.getCurrentUser();
 // Lu thng tin ngi dng vo Realtime Database
 SaveUserInformation(i_NewUser, m_User.getUid());
 // Gi callback thnh cng vi thng tin ngi dng
 callback.onSuccess(m_User);
 }
 else
 {
 // Nu ng k tht bi, gi callback tht bi vi thng bo li
 callback.onFailure(Objects.requireNonNull(task.getException()).getMessage());
 }
 }
 });
 }

 /**
 * Lu thng tin chi tit ca ngi dng vo Realtime Database
 * @param i_NewUser i tng User cha thng tin ngi dng
 * @param i_Uid ID ngi dng t Firebase Authentication
 */
 private void SaveUserInformation(User i_NewUser, String i_Uid)
 {
 // To ng dn n node ca ngi dng da trn UID
 DatabaseReference userReference = m_Ref.child(i_Uid);

 // Lu thng tin c bn ca ngi dng
 userReference.child("firstName").setValue(i_NewUser.getFirstName());
 userReference.child("lastName").setValue(i_NewUser.getLastName());
 userReference.child("email").setValue(i_NewUser.getEmail());
 userReference.child("phone").setValue(i_NewUser.getPhone());

 // Khi to gi hng rng
 userReference.child("shoppingCart").setValue(new HashMap<String, Item>());

 // Khi to danh sch sn phm yu thch rng
 userReference.child("likedItems").setValue(new HashMap<String, Item>());
 }

 /**
 * Ly thng tin chi tit ca ngi dng hin ti t Realtime Database
 * @param callback Interface callback phn hi kt qu
 */
 public void GetUser(UserCallback callback)
 {
 // Kim tra ngi dng ng nhp cha
 if(m_User != null)
 {
 // To ng dn n node ca ngi dng da trn UID
 DatabaseReference userReference = m_Ref.child(m_User.getUid());

 // Ly d liu mt ln t Firebase
 userReference.addListenerForSingleValueEvent(new ValueEventListener() {
 @Override
 public void onDataChange(@NonNull DataSnapshot dataSnapshot) {
 // Ly cc gi tr t snapshot
 String firstName = dataSnapshot.child("firstName").getValue(String.class);
 String lastName = dataSnapshot.child("lastName").getValue(String.class);
 String email = dataSnapshot.child("email").getValue(String.class);
 String phone = dataSnapshot.child("phone").getValue(String.class);

 // To i tng User mi t d liu
 User user = new User(firstName, lastName, "", email, phone);

 // Gi callback vi i tng User to
 callback.onUserFetched(user);
 }

 @Override
 public void onCancelled(@NonNull DatabaseError databaseError) {
 // Gi callback tht bi nu c li
 callback.onFailure();
 }
 });
 }
 else
 {
 // Nu khng c ngi dng ng nhp, gi callback tht bi
 callback.onFailure();
 }
 }

 /**
 * Cp nht gi hng ca ngi dng vo Realtime Database
 * @param i_UserShoppingCart Gi hng mi cn cp nht
 * @param callback Interface callback phn hi kt qu
 */
 public void UpdateShoppingCart(ShoppingCart i_UserShoppingCart, UpdateShoppingCartCallback callback)
 {
 // Kim tra ngi dng ng nhp cha
 if(m_User != null)
 {
 try
 {
 // To ng dn n node gi hng ca ngi dng
 DatabaseReference userShoppingCartReference = m_Ref.child(m_User.getUid()).child("shoppingCart");
 // Cp nht gi hng vi gi tr mi
 userShoppingCartReference.setValue(i_UserShoppingCart.getShoppingCart())
 .addOnCompleteListener(new OnCompleteListener<Void>() {
 @Override
 public void onComplete(@NonNull Task<Void> task) {
 if(task.isSuccessful())
 {
 // Gi callback thnh cng nu cp nht thnh cng
 callback.onSuccess();
 }
 else
 {
 // Gi callback tht bi vi thng bo li
 callback.onFailure(Objects.requireNonNull(task.getException()).getMessage());
 }
 }
 });
 }
 catch(Exception e)
 {
 // Ghi log v gi callback tht bi nu c ngoi l
 Log.e(TAG, "Error updating shopping cart: " + e.getMessage());
 callback.onFailure(e.getMessage());
 }
 }
 else
 {
 // Nu khng c ngi dng ng nhp, gi callback tht bi
 callback.onFailure("User is not logged in");
 }
 }

 /**
 * Cp nht danh sch sn phm yu thch ca ngi dng vo Realtime Database
 * @param i_UserLikedItemsList Danh sch sn phm yu thch mi cn cp nht
 * @param callback Interface callback phn hi kt qu
 */
 public void UpdateLikedItemsList(HashMap<String, Item> i_UserLikedItemsList, UpdateLikedItemsListCallback callback)
 {
 // Kim tra ngi dng ng nhp cha
 if(m_User != null)
 {
 try
 {
 // To ng dn n node danh sch sn phm yu thch ca ngi dng
 DatabaseReference userLikedItemsListReference = m_Ref.child(m_User.getUid()).child("likedItems");
 // Cp nht danh sch vi gi tr mi
 userLikedItemsListReference.setValue(i_UserLikedItemsList)
 .addOnCompleteListener(new OnCompleteListener<Void>() {
 @Override
 public void onComplete(@NonNull Task<Void> task) {
 if(task.isSuccessful())
 {
 // Gi callback thnh cng nu cp nht thnh cng
 callback.onSuccess();
 }
 else
 {
 // Gi callback tht bi vi thng bo li
 callback.onFailure(Objects.requireNonNull(task.getException()).getMessage());
 }
 }
 });
 }
 catch(Exception e)
 {
 // Ghi log v gi callback tht bi nu c ngoi l
 Log.e(TAG, "Error updating liked items list: " + e.getMessage());
 callback.onFailure(e.getMessage());
 }
 }
 else
 {
 // Nu khng c ngi dng ng nhp, gi callback tht bi
 callback.onFailure("User is not logged in");
 }
 }

 /**
 * Ly gi hng ca ngi dng hin ti t Realtime Database
 * @param callback Interface callback phn hi kt qu
 */
 public void GetUserShoppingCart(ShoppingCartCallback callback)
 {
 // Kim tra ngi dng ng nhp cha
 if(m_User != null)
 {
 // To ng dn n node gi hng ca ngi dng
 DatabaseReference userShoppingCartReference = m_Ref.child(m_User.getUid()).child("shoppingCart");

 // Ly d liu mt ln t Firebase
 userShoppingCartReference.addListenerForSingleValueEvent(new ValueEventListener() {
 @Override
 public void onDataChange(@NonNull DataSnapshot dataSnapshot) {
 try {
 // To i tng gi hng mi
 ShoppingCart shoppingCart = new ShoppingCart();
 // Duyt qua tng sn phm trong gi hng
 for(DataSnapshot itemSnapshot : dataSnapshot.getChildren()) {
 // Ly thng tin sn phm
 String itemName = itemSnapshot.child("name").getValue(String.class);
 String itemDescription = itemSnapshot.child("description").getValue(String.class);
 double itemPrice = itemSnapshot.child("price").getValue(Double.class);
 String itemImage = itemSnapshot.child("image").getValue(String.class);
 boolean itemInStock = itemSnapshot.child("inStock").getValue(Boolean.class);
 String itemCategory = itemSnapshot.child("categoryId").getValue(String.class);
 int itemQuantity = itemSnapshot.child("quantity").getValue(Integer.class);

 // To i tng Item t d liu
 Item item = new Item(itemName, itemDescription, itemPrice, itemImage, itemInStock, itemCategory);
 item.setQuantity(itemQuantity);

 // Thm vo gi hng
 shoppingCart.addItem(item);
 }
 // Gi callback vi gi hng to
 callback.onShoppingCartFetched(shoppingCart);
 }
 catch (Exception e) {
 // Ghi log v gi callback tht bi nu c ngoi l
 Log.e(TAG, "Error fetching shopping cart: " + e.getMessage());
 callback.onFailure();
 }
 }

 @Override
 public void onCancelled(@NonNull DatabaseError databaseError) {
 // Gi callback tht bi nu c li t Firebase
 callback.onFailure();
 }
 });
 }
 else
 {
 // Nu khng c ngi dng ng nhp, gi callback tht bi
 callback.onFailure();
 }
 }

 /**
 * Ly danh sch sn phm yu thch ca ngi dng hin ti t Realtime Database
 * @param callback Interface callback phn hi kt qu
 */
 public void GetUserLikedItemsList(LikedItemsCallback callback)
 {
 // Kim tra ngi dng ng nhp cha
 if(m_User != null)
 {
 // To ng dn n node danh sch sn phm yu thch ca ngi dng
 DatabaseReference userLikedItemsListReference = m_Ref.child(m_User.getUid()).child("likedItems");

 // Ly d liu mt ln t Firebase
 userLikedItemsListReference.addListenerForSingleValueEvent(new ValueEventListener() {
 @Override
 public void onDataChange(@NonNull DataSnapshot dataSnapshot) {
 try {
 // To HashMap lu danh sch sn phm yu thch
 HashMap<String, Item> likedItems = new HashMap<>();

 // Duyt qua tng sn phm yu thch
 for(DataSnapshot itemSnapshot : dataSnapshot.getChildren()) {
 // Ly thng tin sn phm
 String itemName = itemSnapshot.child("name").getValue(String.class);
 String itemDescription = itemSnapshot.child("description").getValue(String.class);
 double itemPrice = itemSnapshot.child("price").getValue(Double.class);
 String itemImage = itemSnapshot.child("image").getValue(String.class);
 boolean itemInStock = itemSnapshot.child("inStock").getValue(Boolean.class);
 String itemCategory = itemSnapshot.child("categoryId").getValue(String.class);
 int itemQuantity = itemSnapshot.child("quantity").getValue(Integer.class);

 // To i tng Item t d liu
 Item item = new Item(itemName, itemDescription, itemPrice, itemImage, itemInStock, itemCategory);
 item.setQuantity(itemQuantity);

