```java:app/src/main/java/com/example/argapp/Classes/User.java
package com.example.argapp.Classes;

/**
 * Lp User i din cho mt ngi dng trong ng dng
 * Lu tr cc thng tin c bn ca ngi dng nh h tn, email, mt khu v s in thoi
 */
public class User {
 private String m_FirstName; // Tn ca ngi dng
 private String m_LastName; // H ca ngi dng
 private String m_Password; // Mt khu ngi dng (khng nn lu trn b nh thit b trong thc t)
 private String m_Email; // a ch email ca ngi dng
 private String m_Phone; // S in thoi ca ngi dng
 private ShoppingCart m_ShoppingCart; // Gi hng ca ngi dng

 /**
 * Constructor y cho i tng User
 * 
 * @param i_FirstName Tn ca ngi dng
 * @param i_LastName H ca ngi dng
 * @param i_Password Mt khu ngi dng
 * @param i_Email a ch email ca ngi dng
 * @param i_Phone S in thoi ca ngi dng
 */
 public User(String i_FirstName, String i_LastName, String i_Password, String i_Email, String i_Phone) {
 this.m_FirstName = i_FirstName; // Khi to tn
 this.m_LastName = i_LastName; // Khi to h
 this.m_Password = i_Password; // Khi to mt khu
 this.m_Email = i_Email; // Khi to email
 this.m_Phone = i_Phone; // Khi to s in thoi
 this.m_ShoppingCart = new ShoppingCart(); // Khi to gi hng rng
 }

 /**
 * Constructor thay th cho trng hp khng c thng tin gi hng
 * 
 * @param i_FirstName Tn ca ngi dng
 * @param i_LastName H ca ngi dng
 * @param i_Email a ch email ca ngi dng
 * @param i_Phone S in thoi ca ngi dng
 * @param i_ShoppingCart Gi hng ca ngi dng
