 private String mParam2;

 /**
 * Constructor rng bt buc cho Fragment
 * Android yu cu mi Fragment phi c constructor khng tham s
 */
 public EditItem() {
 // Required empty public constructor
 }

 /**
 * Phng thc factory to mt instance mi ca fragment vi tham s
 * y l cch c khuyn ngh to Fragment vi cc tham s
 *
 * @param param1 Parameter 1
 * @param param2 Parameter 2
 * @return Fragment EditItem mi c to vi cc tham s
 */
