 /**
 * c gi to giao din ca Fragment
 * y l ni inflate layout v thit lp UI
 */
 @Override
 public View onCreateView(LayoutInflater inflater, ViewGroup container,
 Bundle savedInstanceState) {
 // Inflate layout cho fragment v lu tr tham chiu n View chnh
 m_View = inflater.inflate(R.layout.edit_item_page, container, false);

 // Ly i tng Item c truyn qua arguments ca Fragment
 m_CurrentItem = (Item) getArguments().getSerializable("item");

 // Ly tham chiu n MainActivity cha Fragment ny
 m_HostedActivity = (MainActivity) requireActivity();

 // nh x cc thnh phn giao din t layout
 m_ItemName = m_View.findViewById(R.id.itemName);
 m_ItemImage = m_View.findViewById(R.id.itemImage);
 m_ItemPrice = m_View.findViewById(R.id.itemPrice);
 m_CloseButton = m_View.findViewById(R.id.closeButton);
 m_AddToCart = m_View.findViewById(R.id.addToCartButton);
 m_LikedButton = m_View.findViewById(R.id.likeButton);
 m_MinusButton = m_View.findViewById(R.id.minusButton);
 m_AddButton = m_View.findViewById(R.id.addButton);
 m_Quantity = m_View.findViewById(R.id.itemQuantity);

 // ng k Fragment ny lm listener cho s kin cp nht gi hng
 m_HostedActivity.SetShoppingCartUpdatedListener(this);

 // Khng cn lng nghe s kin cp nht danh sch yu thch nn t l null
 m_HostedActivity.SetLikedItemsListUpdateListener(null);

 // Ly thng tin gi hng v danh sch yu thch ca ngi dng t MainActivity
 getUserDetails();

 // Hin th thng tin sn phm ln giao din
 showItem();

 // Thit lp listener cho nt "Thm vo gi hng"
 m_AddToCart.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 // Ly s lng hin ti t TextView
 int quantity = Integer.parseInt(m_Quantity.getText().toString());

 // Thm sn phm vo gi hng vi s lng chn
 m_UserShoppingCart.AddToCart(m_CurrentItem, quantity);

 // Nu sn phm ny cng ang nm trong danh sch yu thch,
 // cp nht thng tin ca n trong danh sch yu thch
 if(m_UserLikedItemsList.containsKey(m_CurrentItem.getName()))
 {
 m_UserLikedItemsList.put(m_CurrentItem.getName(), m_CurrentItem);
 }

 // Cp nht gi hng v danh sch yu thch trong MainActivity
 m_HostedActivity.UpdateShoppingCart(m_UserShoppingCart);
 m_HostedActivity.UpdateLikedItemsList(m_UserLikedItemsList);
 }
 });

 // Thit lp listener cho nt "Yu thch"
 m_LikedButton.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 if(m_UserLikedItemsList.containsKey(m_CurrentItem.getName()))
 {
 // Nu sn phm c yu thch, xa khi danh sch v i icon thnh tri tim trng
 m_UserLikedItemsList.remove(m_CurrentItem.getName());
 m_LikedButton.setImageResource(R.drawable.blank_heart);
 }
 else
 {
 // Nu sn phm cha c yu thch, thm vo danh sch v i icon thnh tri tim y
 // Kim tra xem sn phm c trong gi hng khng ly s lng chnh xc
 HashMap<String, Item> userShoppingCart = m_UserShoppingCart.getShoppingCart();
 if(userShoppingCart.containsKey(m_CurrentItem.getName()))
 {
 m_CurrentItem.setQuantity(userShoppingCart.get(m_CurrentItem.getName()).getQuantity());
 }

 m_UserLikedItemsList.put(m_CurrentItem.getName(), m_CurrentItem);
 m_LikedButton.setImageResource(R.drawable.filled_heart);
 }

 // Cp nht danh sch yu thch trong MainActivity
 m_HostedActivity.UpdateLikedItemsList(m_UserLikedItemsList);
 }
 });

 // Thit lp listener cho nt "Tng s lng"
 m_AddButton.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 int qunatity = Integer.parseInt(m_Quantity.getText().toString());
 qunatity += 1; // Tng s lng ln 1
 m_Quantity.setText(qunatity + ""); // Cp nht hin th s lng

 // Cp nht trng thi nt gim s lng da trn s lng mi
 updateMinusButtonState(qunatity);
 }
 });

 // Thit lp listener cho nt "Gim s lng"
 m_MinusButton.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 int qunatity = Integer.parseInt(m_Quantity.getText().toString());
 qunatity -= 1; // Gim s lng xung 1
 m_Quantity.setText(qunatity + ""); // Cp nht hin th s lng

 // Cp nht trng thi nt gim s lng da trn s lng mi
 updateMinusButtonState(qunatity);
 }
 });

 // Thit lp listener cho nt "ng"
 m_CloseButton.setOnClickListener(new View.OnClickListener() {
 @Override
 public void onClick(View v) {
 // ng Fragment hin ti v quay li Fragment trc bng cch pop back stack
 requireActivity().getSupportFragmentManager().popBackStack();
 }
 });

 return m_View;
 }

 /**
 * Cp nht trng thi ca nt gim s lng
 * V hiu ha nt khi s lng l 1 v kch hot khi s lng > 1
 * @param quantity S lng hin ti
 */
 private void updateMinusButtonState(int quantity) {
 if (quantity <= 1) {
 // Nu s lng <= 1, v hiu ha nt gim
 m_MinusButton.setEnabled(false);
 m_MinusButton.setAlpha(0.5f); // Lm m nt
 m_MinusButton.setBackgroundColor(0xB0B0B0); // i mu nn thnh xm
 } else {
 // Nu s lng > 1, kch hot nt gim
 m_MinusButton.setEnabled(true);
 m_MinusButton.setAlpha(1.0f); // Hin th nt r nt
 m_MinusButton.setBackgroundColor(0x3D3C3C); // i mu nn thnh mu gc
 }
 }

 /**
 * Ly thng tin gi hng v danh sch yu thch ca ngi dng t MainActivity
 */
 private void getUserDetails() {
 m_UserShoppingCart = m_HostedActivity.GetUserShoppingCart();
 m_UserLikedItemsList = m_HostedActivity.GetUserLikedItemsList();
 }

 /**
 * Hin th thng tin sn phm ln giao din
 */
 private void showItem()
 {
 // Ly ID resource ca hnh nh sn phm t tn file
 int itemImage = m_HostedActivity.getResources().getIdentifier(m_CurrentItem.getImage(), "drawable", m_HostedActivity.getPackageName());

 // Hin th thng tin sn phm ln giao din
 m_ItemName.setText(m_CurrentItem.getName()); // Tn sn phm
 m_ItemImage.setImageResource(itemImage); // Hnh nh sn phm
 m_ItemPrice.setText(String.format("%.2f", m_CurrentItem.getPrice())); // Gi sn phm (nh dng 2 s thp phn)

 // Cp nht trng thi nt yu thch da trn vic sn phm c trong danh sch yu thch hay khng
 if(isItemLiked())
 {
 m_LikedButton.setImageResource(R.drawable.filled_heart); // Hin th tri tim y nu sn phm c yu thch
 }
 else
 {
 m_LikedButton.setImageResource(R.drawable.blank_heart); // Hin th tri tim trng nu sn phm cha c yu thch
 }

 // Cp nht trng thi nt gim vi s lng mc nh l 1
 updateMinusButtonState(1);
 }

 /**
 * Kim tra xem sn phm hin ti c trong danh sch yu thch hay khng
 * @return true nu sn phm c yu thch, false nu khng
 */
 private boolean isItemLiked()
 {
 return m_UserLikedItemsList.containsKey(m_CurrentItem.getName());
 }

 /**
 * c gi khi gi hng c cp nht
 * Trin khai t interface OnShoppingCartUpdatedListener
 */
 @Override
 public void OnShoppingCartUpdated() {
 // Hin th thng bo cho ngi dng bit sn phm c thm vo gi hng
 Toast.makeText(getContext(), "Item has been added to cart", Toast.LENGTH_SHORT).show();
 }
}
