```java:app/src/main/java/com/example/argapp/Fragments/HomeFragment.java
package com.example.argapp.Fragments;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ProgressBar;
import android.widget.Toast;

import androidx.fragment.app.Fragment;
import androidx.navigation.Navigation;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.argapp.Activities.MainActivity;
import com.example.argapp.Adapters.CategoriesAdapter;
import com.example.argapp.Adapters.CategoryItemsAdapter;
import com.example.argapp.Classes.CategoriesList;
import com.example.argapp.Classes.Category;
import com.example.argapp.Classes.CategoryItems;
import com.example.argapp.Classes.Item;
import com.example.argapp.Classes.ShoppingCart;
import com.example.argapp.Interfaces.OnCategoriesFetchedListener;
import com.example.argapp.Interfaces.OnCategoryClickListener;
import com.example.argapp.Interfaces.OnCategoryItemsFetchedListener;
import com.example.argapp.Interfaces.OnItemListener;
import com.example.argapp.R;

import java.util.HashMap;
import java.util.List;

/**
 * Fragment hin th trang ch ca ng dng
 * Bao gm danh sch cc danh mc v cc sn phm ni bt
 */
public class HomeFragment extends Fragment implements OnCategoryClickListener, OnItemListener {

 // Cc thnh phn giao din
 private View m_View; // View chnh ca fragment
 private RecyclerView m_CategoriesRV; // RecyclerView hin th danh sch danh mc
 private RecyclerView m_NewArrivalsRV; // RecyclerView hin th sn phm mi
 private ProgressBar m_ProgressBar; // ProgressBar hin th khi ang ti d liu

 // D liu
 private ShoppingCart m_UserShoppingCart; // Gi hng ca ngi dng
 private HashMap<String, Item> m_UserLikedItemsList; // Danh sch sn phm yu thch
 private List<Category> m_CategoriesList; // Danh sch cc danh mc
 private List<Item> m_NewArrivalItems; // Danh sch sn phm mi
 private String m_SelectedCategoryID; // ID ca danh mc c chn

 // Activity cha fragment
 private MainActivity m_HostedActivity; // Activity chnh cha Fragment ny

 /**
 * Constructor rng bt buc cho Fragment
 * Android yu cu mi Fragment phi c constructor khng tham s
 */
 public HomeFragment() {
 // Required empty public constructor
 }

 /**
 * c gi khi Fragment c to
 * Dng khi phc trng thi hoc khi to cc thnh phn khng phi UI
 */
 @Override
 public void onCreate(Bundle savedInstanceState) {
 super.onCreate(savedInstanceState);
 }

 /**
 * c gi to giao din ca Fragment
 * y l ni inflate layout v thit lp UI
 */
 @Override
 public View onCreateView(LayoutInflater inflater, ViewGroup container,
 Bundle savedInstanceState) {
 // Inflate layout cho fragment v lu tr tham chiu n View chnh
 m_View = inflater.inflate(R.layout.fragment_home, container, false);

 // Ly tham chiu n MainActivity cha Fragment ny
 m_HostedActivity = (MainActivity) requireActivity();

 // Khi to cc thnh phn giao din
 initialize();

 // Ti d liu danh mc v sn phm mi t c s d liu
 fetchCategories();
 fetchNewestItems();

 return m_View;
 }

 /**
 * Khi to cc thnh phn giao din v d liu cn thit
 */
 private void initialize() {
 // nh x cc thnh phn giao din t layout
 m_CategoriesRV = m_View.findViewById(R.id.categoriesRecyclerView);
 m_NewArrivalsRV = m_View.findViewById(R.id.newArrivalsRecyclerView);
 m_ProgressBar = m_View.findViewById(R.id.progressBar);

 // Thit lp LayoutManager cho RecyclerView danh mc (nm ngang)
 m_CategoriesRV.setLayoutManager(new LinearLayoutManager(getContext(), LinearLayoutManager.HORIZONTAL, false));

 // Thit lp LayoutManager cho RecyclerView sn phm mi (nm ngang)
 m_NewArrivalsRV.setLayoutManager(new LinearLayoutManager(getContext(), LinearLayoutManager.HORIZONTAL, false));

 // Ly gi hng v danh sch yu thch t MainActivity
 if (m_HostedActivity != null) {
 m_UserShoppingCart = m_HostedActivity.GetUserShoppingCart();
 m_UserLikedItemsList = m_HostedActivity.GetUserLikedItemsList();
 }
 }

 /**
 * Ti d liu danh mc t c s d liu
 */
 private void fetchCategories() {
 // Hin th ProgressBar khi bt u ti d liu
 m_ProgressBar.setVisibility(View.VISIBLE);

 // Gi phng thc tnh ly danh sch danh mc
 CategoriesList.GetCategoriesList(getContext(), new OnCategoriesFetchedListener() {
 @Override
 public void onCategoriesFetched(List<Category> categoriesList) {
 // Lu danh sch danh mc
 m_CategoriesList = categoriesList;

 // Thit lp Adapter cho RecyclerView danh mc
 CategoriesAdapter adapter = new CategoriesAdapter(m_HostedActivity, m_CategoriesList, HomeFragment.this);
 m_CategoriesRV.setAdapter(adapter);

 // n ProgressBar khi ti xong
 m_ProgressBar.setVisibility(View.GONE);
 }
 });
 }

 /**
 * Ti d liu sn phm mi t c s d liu
 * Trong trng hp ny, s dng danh mc "Furniture" lm sn phm mi
 */
 private void fetchNewestItems() {
 // Hin th ProgressBar khi bt u ti d liu
 m_ProgressBar.setVisibility(View.VISIBLE);

 // Gi phng thc tnh ly cc sn phm t danh mc Furniture (ID: 100)
 CategoryItems.GetCategoryItems(getContext(), "100", new OnCategoryItemsFetchedListener() {
 @Override
 public void onCategoryItemsFetched(List<Item> itemsList) {
 // Lu danh sch sn phm mi
 m_NewArrivalItems = itemsList;

 // Thit lp Adapter cho RecyclerView sn phm mi
 CategoryItemsAdapter adapter = new CategoryItemsAdapter(m_HostedActivity, m_NewArrivalItems, m_UserShoppingCart, m_UserLikedItemsList, HomeFragment.this);
 m_NewArrivalsRV.setAdapter(adapter);

 // n ProgressBar khi ti xong
 m_ProgressBar.setVisibility(View.GONE);
 }
 });
