```java:app/src/main/java/com/example/argapp/Fragments/LikedItemsFragment.java
package com.example.argapp.Fragments;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.LinearLayout;
import android.widget.TextView;

import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.argapp.Activities.MainActivity;
import com.example.argapp.Adapters.LikedItemsAdapter;
import com.example.argapp.Classes.Item;
import com.example.argapp.Classes.ShoppingCart;
import com.example.argapp.Interfaces.OnItemListener;
import com.example.argapp.Interfaces.OnLikedItemsListUpdatedListener;
import com.example.argapp.Interfaces.OnShoppingCartUpdatedListener;
import com.example.argapp.R;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;

/**
 * Fragment hin th danh sch cc sn phm c ngi dng nh du yu thch
 * Cho php ngi dng xem, qun l v thao tc vi cc sn phm yu thch
 */
public class LikedItemsFragment extends Fragment implements OnItemListener, OnLikedItemsListUpdatedListener, OnShoppingCartUpdatedListener {

 // Cc thnh phn giao din
 private RecyclerView m_LikedItemsRecyclerView; // RecyclerView hin th danh sch sn phm yu thch
 private LinearLayout m_EmptyLikedItemsLayout; // Layout hin th khi khng c sn phm yu thch
 private TextView m_LikedItemsCount; // TextView hin th s lng sn phm yu thch

 // Adapter v d liu
 private LikedItemsAdapter m_LikedItemsAdapter; // Adapter cho RecyclerView
 private List<Item> m_LikedItemsList; // Danh sch cc sn phm yu thch
 private HashMap<String, Item> m_LikedItemsMap; // Map lu tr sn phm yu thch vi key l tn sn phm
 private ShoppingCart m_UserShoppingCart; // Gi hng ca ngi dng

 // Activity cha fragment
 private MainActivity m_HostedActivity; // Activity chnh cha Fragment ny
 private View m_View; // View chnh ca fragment

 /**
 * Constructor rng bt buc cho Fragment
 * Android yu cu mi Fragment phi c constructor khng tham s
 */
 public LikedItemsFragment() {
 // Required empty public constructor
 }

 /**
 * c gi khi fragment c to
 * Khi to cc bin thnh vin khng phi giao din
 */
 @Override
 public void onCreate(Bundle savedInstanceState) {
 super.onCreate(savedInstanceState);
 }

 /**
 * c gi to giao din ngi dng ca fragment
 * Inflate layout v thit lp cc thnh phn UI
 */
 @Override
 public View onCreateView(LayoutInflater inflater, ViewGroup container,
 Bundle savedInstanceState) {
 // Inflate layout cho fragment
 m_View = inflater.inflate(R.layout.fragment_liked_items, container, false);

 // Ly tham chiu n Activity cha Fragment
 m_HostedActivity = (MainActivity) requireActivity();

 // Khi to cc thnh phn giao din v d liu
 initialize();

 // Hin th danh sch sn phm yu thch
 displayLikedItems();

 return m_View;
 }

 /**
 * c gi khi fragment tr nn hin th vi ngi dng
 * ng k cc listener theo di s thay i d liu
 */
 @Override
 public void onResume() {
 super.onResume();

 // ng k fragment ny lng nghe s kin cp nht gi hng
 m_HostedActivity.SetShoppingCartUpdatedListener(this);

 // ng k fragment ny lng nghe s kin cp nht danh sch yu thch
 m_HostedActivity.SetLikedItemsListUpdateListener(this);
 }

 /**
 * Khi to cc thnh phn giao din v d liu
 */
 private void initialize() {
 // nh x cc thnh phn giao din t layout
 m_LikedItemsRecyclerView = m_View.findViewById(R.id.likedItemsRecyclerView);
 m_EmptyLikedItemsLayout = m_View.findViewById(R.id.emptyLikedItemsLayout);
 m_LikedItemsCount = m_View.findViewById(R.id.likedItemsCount);

 // Thit lp LayoutManager cho RecyclerView
 m_LikedItemsRecyclerView.setLayoutManager(new LinearLayoutManager(getContext()));

 // Ly gi hng v danh sch sn phm yu thch t MainActivity
 m_UserShoppingCart = m_HostedActivity.GetUserShoppingCart();
 m_LikedItemsMap = m_HostedActivity.GetUserLikedItemsList();

 // Khi to danh sch cc sn phm yu thch t HashMap
 m_LikedItemsList = new ArrayList<>(m_LikedItemsMap.values());
 }

 /**
 * Hin th danh sch sn phm yu thch
 * Hoc hin th thng bo nu khng c sn phm yu thch
 */
 private void displayLikedItems() {
 // Cp nht s lng sn phm yu thch hin th trn UI
 m_LikedItemsCount.setText("(" + m_LikedItemsList.size() + ")");

 // Kim tra xem c sn phm yu thch no khng
 if (m_LikedItemsList.isEmpty()) {
 // Nu khng c, hin th layout trng
 m_EmptyLikedItemsLayout.setVisibility(View.VISIBLE);
 m_LikedItemsRecyclerView.setVisibility(View.GONE);
 } else {
 // Nu c, hin th danh sch sn phm
 m_EmptyLikedItemsLayout.setVisibility(View.GONE);
 m_LikedItemsRecyclerView.setVisibility(View.VISIBLE);

 // To adapter mi cho RecyclerView vi danh sch sn phm yu thch
 m_LikedItemsAdapter = new LikedItemsAdapter(m_LikedItemsList, m_HostedActivity, this);
 m_LikedItemsRecyclerView.setAdapter(m_LikedItemsAdapter);
 }
 }

 /**
 * c gi khi ngi dng click vo mt sn phm trong danh sch
 * @param item i tng Item c click
 */
 @Override
 public void onItemClick(Item item) {
 // To Bundle truyn d liu sang EditItem Fragment
 Bundle args = new Bundle();
 args.putSerializable("item", item);

 // To mt instance ca EditItem Fragment
 EditItem editItemFragment = new EditItem();
 editItemFragment.setArguments(args);

 // Thm EditItem Fragment vo back stack v hin th n
 requireActivity().getSupportFragmentManager().beginTransaction()
 .replace(R.id.fragmentContainer, editItemFragment)
 .addToBackStack(null)
 .commit();
 }

 /**
 * c gi khi ngi dng click vo nt "Thm vo gi hng"
 * @param item i tng Item cn thm vo gi hng
 */
 @Override
 public void onAddToCartClick(Item item) {
 // Thm sn phm vo gi hng
 m_UserShoppingCart.addItem(item);

 // Cp nht gi hng trong MainActivity
 m_HostedActivity.UpdateShoppingCart(m_UserShoppingCart);
 }

 /**
 * c gi khi ngi dng click vo nt "Yu thch"
 * @param item i tng Item cn thm/xa khi danh sch yu thch
 */
 @Override
 public void onLikeClick(Item item) {
 // Kim tra xem sn phm c trong danh sch yu thch khng
 if (m_LikedItemsMap.containsKey(item.getName())) {
 // Nu c, xa khi danh sch yu thch
 m_LikedItemsMap.remove(item.getName());
 } else {
 // Nu khng, thm vo danh sch yu thch
 m_LikedItemsMap.put(item.getName(), item);
 }

 // Cp nht danh sch sn phm yu thch
 m_LikedItemsList = new ArrayList<>(m_LikedItemsMap.values());

 // Cp nht danh sch yu thch trong MainActivity
 m_HostedActivity.UpdateLikedItemsList(m_LikedItemsMap);

 // Cp nht giao din hin th thay i
 displayLikedItems();
 }

 /**
 * c gi khi danh sch sn phm yu thch c cp nht
 * Trin khai t interface OnLikedItemsListUpdatedListener
 */
 @Override
 public void OnLikedItemsListUpdated() {
 // Ly danh sch yu thch mi t MainActivity
 m_LikedItemsMap = m_HostedActivity.GetUserLikedItemsList();

 // Cp nht danh sch sn phm yu thch
 m_LikedItemsList = new ArrayList<>(m_LikedItemsMap.values());

 // Cp nht giao din hin th thay i
 displayLikedItems();
 }

 /**
 * c gi khi gi hng c cp nht
 * Trin khai t interface OnShoppingCartUpdatedListener
 */
 @Override
 public void OnShoppingCartUpdated() {
 // Ly gi hng mi t MainActivity
 m_UserShoppingCart = m_HostedActivity.GetUserShoppingCart();

 // Nu adapter c khi to, thng bo cho n cp nht d liu
 if (m_LikedItemsAdapter != null) {
 m_LikedItemsAdapter.notifyDataSetChanged();
 }
 }
}
