 // Ly gi hng mi t MainActivity
 m_UserShoppingCart = m_HostedActivity.GetUserShoppingCart();
 // Cp nht danh sch sn phm trong gi hng
 m_ShoppingCartAsList = m_UserShoppingCart.getShoppingCartAsList();
 // Cp nht giao din hin th
 displayCart();
 }
