package com.example.argapp.Interfaces;

import com.example.argapp.Classes.Item;

/**
 * Interface OnItemListener nh ngha cc phng thc callback 
 * x l cc tng tc ca ngi dng vi mt sn phm c th
 * 
 * Interface ny gom nhm cc tng tc ph bin vi sn phm nh nhp vo sn phm,
 * thm vo gi hng, v yu thch/b yu thch, gip cc lp thc hin c th
 * x l cc tng tc ny theo cch ring ca h
 */
public interface OnItemListener {

 /**
 * c gi khi ngi dng nhp vo mt sn phm
 * 
 * @param item i tng Item i din cho sn phm c nhp vo
 */
 void onItemClick(Item item);

 /**
 * c gi khi ngi dng nhp vo nt thm vo gi hng
 * 
 * @param item i tng Item i din cho sn phm cn thm vo gi hng
 */
 void onAddToCartClick(Item item);

 /**
 * c gi khi ngi dng nhp vo nt yu thch/b yu thch
 * 
 * @param item i tng Item i din cho sn phm cn thay i trng thi yu thch
 */
 void onLikeClick(Item item);
}
