Tiếp theo, cần tạo một Adapter để quản lý danh sách bình luận:

```java:app/src/main/java/com/example/argapp/Adapters/ReviewsAdapter.java
package com.example.argapp.Adapters;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.LinearLayout;
import android.widget.RatingBar;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.example.argapp.Models.Review;
import com.example.argapp.R;

import java.util.List;

import de.hdodenhof.circleimageview.CircleImageView;

public class ReviewsAdapter extends RecyclerView.Adapter<ReviewsAdapter.ReviewViewHolder> {
 private List<Review> reviewsList;
 private OnReviewInteractionListener listener;

 public interface OnReviewInteractionListener {
 void onLikeClicked(int position);
 void onReplyClicked(int position);
 void onReviewClicked(int position);
 }

 public ReviewsAdapter(List<Review> reviewsList, OnReviewInteractionListener listener) {
 this.reviewsList = reviewsList;
 this.listener = listener;
 }

 @NonNull
 @Override
 public ReviewViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
 View view = LayoutInflater.from(parent.getContext())
 .inflate(R.layout.review_item, parent, false);
 return new ReviewViewHolder(view);
 }

 @Override
 public void onBindViewHolder(@NonNull ReviewViewHolder holder, int position) {
 Review review = reviewsList.get(position);

 holder.reviewerName.setText(review.getUserName());
 holder.reviewDate.setText(review.getDate());
 holder.reviewRating.setRating(review.getRating());
 holder.reviewContent.setText(review.getContent());
 holder.likeCount.setText(String.valueOf(review.getLikeCount()));

 // Cài đặt ảnh đại diện
 if (review.getUserAvatarResId() != 0) {
 holder.reviewerAvatar.setImageResource(review.getUserAvatarResId());
 }

 // Thêm ảnh đính kèm nếu có
 holder.reviewImageContainer.removeAllViews();
 if (review.getAttachedImages() != null && !review.getAttachedImages().isEmpty()) {
 holder.reviewImageContainer.setVisibility(View.VISIBLE);
 for (int imageResId : review.getAttachedImages()) {
 ImageView imageView = new ImageView(holder.itemView.getContext());
 LinearLayout.LayoutParams params = new LinearLayout.LayoutParams(
 ViewGroup.LayoutParams.WRAP_CONTENT, 
 ViewGroup.LayoutParams.WRAP_CONTENT);
 params.width = 200; // 200dp
 params.height = 200; // 200dp
 params.setMargins(0, 0, 8, 0);
 imageView.setLayoutParams(params);
 imageView.setScaleType(ImageView.ScaleType.CENTER_CROP);
 imageView.setImageResource(imageResId);
 holder.reviewImageContainer.addView(imageView);
 }
 } else {
 holder.reviewImageContainer.setVisibility(View.GONE);
 }

 // Cài đặt listener
 final int pos = position;
 holder.likeReviewButton.setOnClickListener(v -> {
 if (listener != null) listener.onLikeClicked(pos);
 });

 holder.replyButton.setOnClickListener(v -> {
 if (listener != null) listener.onReplyClicked(pos);
 });

 holder.itemView.setOnClickListener(v -> {
 if (listener != null) listener.onReviewClicked(pos);
 });
 }

 @Override
 public int getItemCount() {
 return reviewsList.size();
 }

 public static class ReviewViewHolder extends RecyclerView.ViewHolder {
 CircleImageView reviewerAvatar;
 TextView reviewerName;
 RatingBar reviewRating;
