package com.example.argapp.Models;

import java.util.List;

public class Review {
 private String id;
 private String userName;
 private int userAvatarResId;
 private float rating;
 private String content;
 private String date;
 private int likeCount;
 private List<Integer> attachedImages;

 public Review(String id, String userName, int userAvatarResId, float rating, 
 String content, String date, int likeCount, List<Integer> attachedImages) {
 this.id = id;
 this.userName = userName;
 this.userAvatarResId = userAvatarResId;
 this.rating = rating;
 this.content = content;
 this.date = date;
 this.likeCount = likeCount;
 this.attachedImages = attachedImages;
 }

 // Getters
 public String getId() {
 return id;
 }

 public String getUserName() {
 return userName;
 }

 public int getUserAvatarResId() {
 return userAvatarResId;
 }

 public float getRating() {
 return rating;
 }

 public String getContent() {
 return content;
 }

 public String getDate() {
 return date;
 }

 public int getLikeCount() {
 return likeCount;
 }

 public List<Integer> getAttachedImages() {
 return attachedImages;
 }

 // Setter cho likeCount (để tăng/giảm số lượt thích)
 public void setLikeCount(int likeCount) {
 this.likeCount = likeCount;
 }
}
