 avatarImageView = view.findViewById(R.id.avatar);
 lastNameInput = view.findViewById(R.id.lastNameInput);
 firstNameInput = view.findViewById(R.id.firstNameInput);
 emailInput = view.findViewById(R.id.emailInput);
 phoneInput = view.findViewById(R.id.phoneInput);
 addressInput = view.findViewById(R.id.addressInput);
 changePasswordBtn = view.findViewById(R.id.changePasswordBtn);
 saveBtn = view.findViewById(R.id.saveBtn);
 backBtn = view.findViewById(R.id.backBtn);
 changePhotoBtn = view.findViewById(R.id.changePhotoBtn);

 // Khởi tạo UserController
 userController = new UserController();

 // Lấy thông tin người dùng hiện tại
 fetchUserData();

 // Xử lý sự kiện khi nhấn nút Quay lại
 backBtn.setOnClickListener(v -> {
 Navigation.findNavController(v).popBackStack();
 });

 // Xử lý sự kiện khi nhấn nút Đổi ảnh đại diện
 changePhotoBtn.setOnClickListener(v -> {
 showImagePickerOptions();
 });

 // Xử lý sự kiện khi nhấn nút Đổi mật khẩu
 changePasswordBtn.setOnClickListener(v -> {
 showChangePasswordDialog();
 });

 // Xử lý sự kiện khi nhấn nút Lưu
 saveBtn.setOnClickListener(v -> {
 saveUserProfile();
 });

 return view;
 }

 // Phương thức lấy thông tin người dùng từ Firebase
 private void fetchUserData() {
 userController.GetUser(new UserModel.UserCallback() {
 @Override
 public void onSuccess(User user) {
 if (user != null && isAdded()) {
 currentUser = user;

 // Hiển thị dữ liệu lên giao diện
 lastNameInput.setText(user.getLastName());
 firstNameInput.setText(user.getFirstName());
 emailInput.setText(user.getEmail());
 phoneInput.setText(user.getPhoneNumber());

 // Kiểm tra và hiển thị địa chỉ nếu có
 if (user.getAddress() != null) {
 addressInput.setText(user.getAddress());
 }

 // Tải ảnh đại diện nếu có
 if (user.getAvatar() != null && !user.getAvatar().isEmpty()) {
 if (user.getAvatar().startsWith("http")) {
 Glide.with(requireContext())
 .load(user.getAvatar())
 .placeholder(R.drawable.doremon)
 .into(avatarImageView);
 } else if (user.getAvatar().startsWith("drawable/")) {
 // Xử lý ảnh từ drawable
 String drawableName = user.getAvatar().replace("drawable/", "");
 int resourceId = getResources().getIdentifier(drawableName, "drawable", requireContext().getPackageName());
 if (resourceId != 0) {
 Glide.with(requireContext())
 .load(resourceId)
 .into(avatarImageView);
 }
 }
 }
 }
 }

 @Override
 public void onFailure(DatabaseError error) {
 if (isAdded()) {
 Toast.makeText(getContext(), "Không thể tải thông tin người dùng: " + error.getMessage(), Toast.LENGTH_SHORT).show();
 }
 }
 });
 }

 // Hiển thị dialog cho phép chọn ảnh từ thư viện hoặc chụp ảnh mới
 private void showImagePickerOptions() {
 String[] options = {"Chụp ảnh mới", "Chọn từ thư viện"};
 AlertDialog.Builder builder = new AlertDialog.Builder(requireContext());
 builder.setTitle("Chọn ảnh đại diện");
 builder.setItems(options, (dialog, which) -> {
 if (which == 0) {
 // Chụp ảnh mới
 Intent takePictureIntent = new Intent(MediaStore.ACTION_IMAGE_CAPTURE);
 if (takePictureIntent.resolveActivity(requireActivity().getPackageManager()) != null) {
 startActivityForResult(takePictureIntent, REQUEST_IMAGE_CAPTURE);
 }
 } else {
 // Chọn từ thư viện
 Intent pickPhotoIntent = new Intent(Intent.ACTION_PICK);
 pickPhotoIntent.setType("image/*");
 startActivityForResult(pickPhotoIntent, REQUEST_IMAGE_PICK);
 }
 });
 builder.show();
 }

 // Xử lý kết quả trả về khi chọn ảnh hoặc chụp ảnh
 @Override
 public void onActivityResult(int requestCode, int resultCode, @Nullable Intent data) {
 super.onActivityResult(requestCode, resultCode, data);

 if (resultCode == Activity.RESULT_OK) {
 if (requestCode == REQUEST_IMAGE_PICK && data != null) {
 // Xử lý ảnh được chọn từ thư viện
 selectedImageUri = data.getData();
 isImageChanged = true;
 avatarImageView.setImageURI(selectedImageUri);
 } else if (requestCode == REQUEST_IMAGE_CAPTURE && data != null) {
 // Xử lý ảnh được chụp từ camera
 Bundle extras = data.getExtras();
 Bitmap imageBitmap = (Bitmap) extras.get("data");
 avatarImageView.setImageBitmap(imageBitmap);

 // Chuyển Bitmap thành Uri
 selectedImageUri = getImageUri(imageBitmap);
 isImageChanged = true;
 }
 }
 }

 // Chuyển Bitmap thành Uri
 private Uri getImageUri(Bitmap bitmap) {
 ByteArrayOutputStream bytes = new ByteArrayOutputStream();
 bitmap.compress(Bitmap.CompressFormat.JPEG, 100, bytes);
 String path = MediaStore.Images.Media.insertImage(requireContext().getContentResolver(), bitmap, "ProfileImage", null);
 return Uri.parse(path);
 }

 // Hiển thị dialog đổi mật khẩu
 private void showChangePasswordDialog() {
 AlertDialog.Builder builder = new AlertDialog.Builder(requireContext());
 View dialogView = getLayoutInflater().inflate(R.layout.dialog_change_password, null);
 builder.setView(dialogView);

 // Lấy các thành phần của dialog
 TextInputEditText currentPasswordInput = dialogView.findViewById(R.id.currentPasswordInput);
 TextInputEditText newPasswordInput = dialogView.findViewById(R.id.newPasswordInput);
 TextInputEditText confirmNewPasswordInput = dialogView.findViewById(R.id.confirmNewPasswordInput);
 Button cancelBtn = dialogView.findViewById(R.id.cancelPasswordBtn);
 Button confirmBtn = dialogView.findViewById(R.id.confirmPasswordBtn);

 // Tạo dialog
 AlertDialog dialog = builder.create();

 // Xử lý sự kiện khi nhấn nút Hủy
 cancelBtn.setOnClickListener(v -> {
 dialog.dismiss();
 });

 // Xử lý sự kiện khi nhấn nút Xác nhận
 confirmBtn.setOnClickListener(v -> {
 // Lấy các giá trị đã nhập
 String currentPassword = currentPasswordInput.getText().toString().trim();
 String newPassword = newPasswordInput.getText().toString().trim();
 String confirmNewPassword = confirmNewPasswordInput.getText().toString().trim();

 // Kiểm tra mật khẩu hiện tại
 if (currentPassword.isEmpty()) {
 currentPasswordInput.setError("Vui lòng nhập mật khẩu hiện tại");
 return;
 }

 // Kiểm tra mật khẩu mới
 if (newPassword.isEmpty()) {
 newPasswordInput.setError("Vui lòng nhập mật khẩu mới");
 return;
 }

 // Kiểm tra xác nhận mật khẩu
 if (confirmNewPassword.isEmpty()) {
 confirmNewPasswordInput.setError("Vui lòng xác nhận mật khẩu mới");
 return;
 }

 // Kiểm tra mật khẩu mới và xác nhận mật khẩu có trùng khớp
 if (!newPassword.equals(confirmNewPassword)) {
 confirmNewPasswordInput.setError("Mật khẩu mới và xác nhận mật khẩu không khớp");
 return;
 }

 // Kiểm tra mật khẩu hiện tại có đúng không
 if (!currentUser.getPassword().equals(currentPassword)) {
 currentPasswordInput.setError("Mật khẩu hiện tại không đúng");
 return;
 }

 // Cập nhật mật khẩu mới
 userController.changePassword(currentPassword, newPassword, new UserModel.PasswordChangeCallback() {
 @Override
 public void onSuccess() {
 // Cập nhật mật khẩu thành công
 Toast.makeText(getContext(), "Đổi mật khẩu thành công", Toast.LENGTH_SHORT).show();
 dialog.dismiss();
 }

 @Override
 public void onFailure(Exception e) {
 // Cập nhật mật khẩu thất bại
 Toast.makeText(getContext(), "Đổi mật khẩu thất bại: " + e.getMessage(), Toast.LENGTH_SHORT).show();
 }
 });
 });

 // Hiển thị dialog
 dialog.show();
 }

 // Lưu thông tin người dùng
 private void saveUserProfile() {
 // Kiểm tra các trường thông tin
 String lastName = lastNameInput.getText().toString().trim();
 String firstName = firstNameInput.getText().toString().trim();
 String phone = phoneInput.getText().toString().trim();
 String address = addressInput.getText().toString().trim();

 if (lastName.isEmpty()) {
 lastNameInput.setError("Vui lòng nhập họ");
 return;
 }

 if (firstName.isEmpty()) {
 firstNameInput.setError("Vui lòng nhập tên");
 return;
 }

 if (phone.isEmpty()) {
 phoneInput.setError("Vui lòng nhập số điện thoại");
 return;
 }

 // Cập nhật thông tin người dùng
 currentUser.setLastName(lastName);
 currentUser.setFirstName(firstName);
 currentUser.setPhoneNumber(phone);
 currentUser.setAddress(address);

 // Nếu có thay đổi ảnh đại diện, tải lên Firebase Storage
 if (isImageChanged && selectedImageUri != null) {
 uploadImage(currentUser);
 } else {
 // Nếu không có thay đổi ảnh, chỉ cập nhật thông tin
 updateUserProfile(currentUser);
 }
 }

 // Tải ảnh lên Firebase Storage
 private void uploadImage(User user) {
 // Hiển thị thông báo đang tải
 Toast.makeText(getContext(), "Đang cập nhật thông tin...", Toast.LENGTH_SHORT).show();

 // Tạo reference đến Storage
 StorageReference storageRef = FirebaseStorage.getInstance().getReference();
 String fileName = "profile_images/" + UUID.randomUUID().toString();
 StorageReference imageRef = storageRef.child(fileName);

 try {
 // Chuyển ảnh thành bitmap để nén
 Bitmap bitmap = MediaStore.Images.Media.getBitmap(requireActivity().getContentResolver(), selectedImageUri);
 ByteArrayOutputStream baos = new ByteArrayOutputStream();
 bitmap.compress(Bitmap.CompressFormat.JPEG, 70, baos);
 byte[] data = baos.toByteArray();

 // Tải lên Firebase Storage
 UploadTask uploadTask = imageRef.putBytes(data);
 uploadTask.addOnSuccessListener(taskSnapshot -> {
 // Lấy URL của ảnh đã tải lên
 imageRef.getDownloadUrl().addOnSuccessListener(uri -> {
 // Cập nhật URL ảnh vào đối tượng User
 user.setAvatar(uri.toString());
 // Cập nhật thông tin người dùng lên Firebase
 updateUserProfile(user);
 }).addOnFailureListener(e -> {
 Toast.makeText(getContext(), "Lỗi khi lấy URL ảnh: " + e.getMessage(), Toast.LENGTH_SHORT).show();
 });
 }).addOnFailureListener(e -> {
 Toast.makeText(getContext(), "Lỗi khi tải ảnh lên: " + e.getMessage(), Toast.LENGTH_SHORT).show();
 });
 } catch (IOException e) {
 Toast.makeText(getContext(), "Lỗi xử lý ảnh: " + e.getMessage(), Toast.LENGTH_SHORT).show();
 }
 }

 // Cập nhật thông tin người dùng lên Firebase
 private void updateUserProfile(User user) {
 userController.updateUserProfile(user, new UserModel.UpdateProfileCallback() {
 @Override
 public void onSuccess() {
 if (isAdded()) {
 Toast.makeText(getContext(), "Cập nhật thông tin thành công", Toast.LENGTH_SHORT).show();
 Navigation.findNavController(requireView()).popBackStack();
 }
 }

 @Override
 public void onFailure(Exception e) {
 if (isAdded()) {
 Toast.makeText(getContext(), "Cập nhật thông tin thất bại: " + e.getMessage(), Toast.LENGTH_SHORT).show();
 }
 }
 });
 }
}
```

