 public Category(String i_Id, String i_Name, String i_Image) {
 this.m_Id = i_Id; // Khởi tạo ID
 this.m_Name = i_Name; // Khởi tạo tên
 this.m_Image = i_Image; // Khởi tạo đường dẫn hình ảnh
