```java:app/src/main/java/com/example/argapp/Classes/CategoryItems.java
package com.example.argapp.Classes;

import android.content.Context;
import android.widget.Toast;

import androidx.annotation.NonNull;

import com.example.argapp.Interfaces.OnCategoryItemsFetchedListener;
import com.google.firebase.database.DataSnapshot;
import com.google.firebase.database.DatabaseError;
import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;
import com.google.firebase.database.ValueEventListener;

import java.util.ArrayList;
import java.util.List;

/**
 * Lớp CategoryItems cung cấp phương thức để truy vấn và lấy danh sách các sản phẩm 
 * thuộc một danh mục cụ thể từ Firebase Database
 */
public class CategoryItems {
 /**
 * Phương thức tĩnh để lấy danh sách các sản phẩm thuộc một danh mục từ Firebase
 * @param context Context để hiển thị thông báo lỗi nếu cần
 * @param i_CategoryId ID của danh mục cần lấy sản phẩm
 * @param callback Interface callback để trả về kết quả sau khi truy vấn
 */
 public static void GetCategoryItems(Context context, String i_CategoryId, OnCategoryItemsFetchedListener callback)
 {
 // Danh sách lưu trữ các sản phẩm thuộc danh mục
 List<Item> categoryItemsList = new ArrayList<>();

 // Khởi tạo tham chiếu đến Firebase Database
 FirebaseDatabase m_Database;
 DatabaseReference m_Ref;

 // Lấy instance của Firebase Database
 m_Database = FirebaseDatabase.getInstance();
 // Chỉ định đường dẫn đến node chứa các sản phẩm của danh mục
 // sử dụng ID danh mục được truyền vào
 m_Ref = m_Database.getReference("Data/" + i_CategoryId);

 // Thêm listener một lần để lấy dữ liệu từ Firebase
 m_Ref.addListenerForSingleValueEvent(new ValueEventListener() {
 /**
 * Được gọi khi dữ liệu được đọc thành công từ Firebase
 * @param snapshot Chứa toàn bộ dữ liệu từ đường dẫn đã chỉ định
 */
 @Override
 public void onDataChange(@NonNull DataSnapshot snapshot) {
 // Xóa dữ liệu cũ để tránh trùng lặp khi tải lại
 categoryItemsList.clear();

 // Duyệt qua tất cả các con (sản phẩm) trong snapshot
 for(DataSnapshot itemSnapshot : snapshot.getChildren())
 {
 // Lấy các thuộc tính của sản phẩm từ Firebase
 String itemName = itemSnapshot.child("Name").getValue(String.class);
 String itemDescription = itemSnapshot.child("Description").getValue(String.class);
 double itemPrice = itemSnapshot.child("Price").getValue(Double.class);
 String itemImage = itemSnapshot.child("Image").getValue(String.class);
 boolean itemInStock = itemSnapshot.child("InStock").getValue(Boolean.class);

 // Tạo đối tượng Item mới với dữ liệu vừa lấy
 Item item = new Item(itemName, itemDescription, itemPrice, itemImage, itemInStock, i_CategoryId);
 // Mặc định số lượng ban đầu là 1
 item.setQuantity(1);
 // Thêm vào danh sách các sản phẩm
 categoryItemsList.add(item);
 }

 // Gọi callback khi quá trình lấy dữ liệu hoàn tất thành công
 callback.onCategoryItemsFetched(categoryItemsList);
 }

 /**
 * Được gọi khi có lỗi xảy ra trong quá trình đọc dữ liệu
 * @param error Chứa thông tin về lỗi đã xảy ra
 */
 @Override
 public void onCancelled(@NonNull DatabaseError error) {
 // Hiển thị thông báo lỗi cho người dùng
 Toast.makeText(context, error.toString(), Toast.LENGTH_SHORT).show();
 // Gọi callback với danh sách trống để thông báo việc không lấy được dữ liệu
 callback.onCategoryItemsFetched(new ArrayList<>());
 }
 });
 }
}
