```java:app/src/main/java/com/example/argapp/Classes/Item.java
package com.example.argapp.Classes;

/**
 * Lớp Item đại diện cho một sản phẩm trong ứng dụng
 * Chứa các thông tin về sản phẩm như tên, mô tả, giá cả, hình ảnh...
 */
public class Item {
 private String m_Name; // Tên của sản phẩm
 private String m_Description; // Mô tả chi tiết về sản phẩm
 private double m_Price; // Giá của sản phẩm
 private String m_Image; // Đường dẫn/tên file hình ảnh của sản phẩm
 private boolean m_IsInStock; // Trạng thái còn hàng hay không
 private String m_CategoryId; // ID của danh mục chứa sản phẩm
 private int m_Quantity; // Số lượng sản phẩm (dùng trong giỏ hàng)

 /**
 * Constructor khởi tạo đối tượng Item với đầy đủ thông tin
 * @param i_Name Tên hiển thị của sản phẩm
 * @param i_Description Mô tả chi tiết về sản phẩm
 * @param i_Price Giá của sản phẩm
 * @param i_Image Đường dẫn/tên file hình ảnh của sản phẩm
 * @param i_IsInStock Trạng thái còn hàng hay không
 * @param i_CategoryId ID của danh mục chứa sản phẩm
 */
 public Item(String i_Name, String i_Description, double i_Price, String i_Image, boolean i_IsInStock, String i_CategoryId) {
 m_Name = i_Name; // Khởi tạo tên sản phẩm
 m_Description = i_Description; // Khởi tạo mô tả
 m_Price = i_Price; // Khởi tạo giá
 m_Image = i_Image; // Khởi tạo đường dẫn hình ảnh
 m_IsInStock = i_IsInStock; // Khởi tạo trạng thái còn hàng
 m_CategoryId = i_CategoryId; // Khởi tạo ID danh mục
 m_Quantity = 1; // Mặc định số lượng ban đầu là 1
 }

 /**
 * Constructor sao chép để tạo bản sao của một đối tượng Item hiện có
 * @param i_Item Đối tượng Item cần sao chép
 */
 public Item(Item i_Item) {
 this.m_Name = i_Item.getName(); // Sao chép tên
 this.m_Description = i_Item.getDescription(); // Sao chép mô tả
 this.m_Price = i_Item.getPrice(); // Sao chép giá
 this.m_Image = i_Item.getImage(); // Sao chép đường dẫn hình ảnh
 this.m_IsInStock = i_Item.getIsInStock(); // Sao chép trạng thái còn hàng
 this.m_CategoryId = i_Item.getCategoryId(); // Sao chép ID danh mục
 this.m_Quantity = i_Item.getQuantity(); // Sao chép số lượng
 }

 /**
 * Phương thức getter để lấy tên của sản phẩm
 * @return Tên hiển thị của sản phẩm
 */
 public String getName() {
 return m_Name;
 }

 /**
 * Phương thức getter để lấy mô tả của sản phẩm
 * @return Mô tả chi tiết về sản phẩm
 */
 public String getDescription() {
 return m_Description;
 }

 /**
 * Phương thức getter để lấy giá của sản phẩm
 * @return Giá của sản phẩm
 */
 public double getPrice() {
 return m_Price;
 }

 /**
 * Phương thức getter để lấy đường dẫn/tên file hình ảnh của sản phẩm
 * @return Đường dẫn/tên file hình ảnh
 */
 public String getImage() {
 return m_Image;
 }

 /**
 * Phương thức getter để kiểm tra trạng thái còn hàng của sản phẩm
 * @return true nếu sản phẩm còn hàng, false nếu hết hàng
 */
 public boolean getIsInStock() {
 return m_IsInStock;
 }

 /**
 * Phương thức getter để lấy ID của danh mục chứa sản phẩm
 * @return ID của danh mục
 */
 public String getCategoryId() {
 return m_CategoryId;
 }

 /**
 * Phương thức getter để lấy số lượng của sản phẩm
 * @return Số lượng sản phẩm
 */
 public int getQuantity() {
 return m_Quantity;
 }

 /**
 * Phương thức setter để thiết lập số lượng của sản phẩm
 * @param i_Quantity Số lượng mới cần thiết lập
 */
 public void setQuantity(int i_Quantity) {
 this.m_Quantity = i_Quantity;
 }

 /**
 * Tăng số lượng sản phẩm lên 1 đơn vị
 */
 public void incrementQuantity() {
 m_Quantity++;
 }

 /**
 * Giảm số lượng sản phẩm xuống 1 đơn vị nếu số lượng hiện tại lớn hơn 1
 * @return true nếu giảm thành công, false nếu số lượng đã là 1 và không thể giảm thêm
 */
 public boolean decrementQuantity() {
 if(m_Quantity > 1) {
 m_Quantity--;
 return true;
 }
 return false;
 }
}
