```java:app/src/main/java/com/example/argapp/Classes/ShoppingCart.java
package com.example.argapp.Classes;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;

/**
 * Lớp ShoppingCart quản lý giỏ hàng của người dùng
 * Cho phép thêm, xóa, cập nhật sản phẩm và tính tổng tiền
 */
public class ShoppingCart {
 // HashMap lưu trữ các sản phẩm trong giỏ hàng, với key là tên sản phẩm (dùng làm ID)
 private HashMap<String, Item> m_ShoppingCartList;

 /**
 * Constructor khởi tạo một giỏ hàng rỗng
 */
 public ShoppingCart()
 {
 m_ShoppingCartList = new HashMap<>();
 }

 /**
 * Thêm một sản phẩm vào giỏ hàng
 * Nếu sản phẩm đã tồn tại trong giỏ, số lượng sẽ được tăng lên thay vì thêm mới
 * @param i_Item Sản phẩm cần thêm vào giỏ hàng
 */
 public void addItem(Item i_Item)
 {
 String itemName = i_Item.getName();

 // Kiểm tra xem sản phẩm đã có trong giỏ hàng chưa
 if(m_ShoppingCartList.containsKey(itemName))
 {
 // Nếu đã có, tăng số lượng lên
 Item itemToIncrement = m_ShoppingCartList.get(itemName);
 itemToIncrement.incrementQuantity();
 }
 else
 {
 // Nếu chưa có, thêm sản phẩm mới vào giỏ hàng với số lượng mặc định là 1
 Item newItem = new Item(i_Item); // Tạo bản sao để tránh tham chiếu đến đối tượng gốc
 newItem.setQuantity(1);
 m_ShoppingCartList.put(itemName, newItem);
 }
 }

 /**
 * Xóa một sản phẩm khỏi giỏ hàng dựa trên tên
 * @param i_ItemName Tên của sản phẩm cần xóa
 * @return true nếu xóa thành công, false nếu không tìm thấy sản phẩm
 */
 public boolean removeItemByName(String i_ItemName)
 {
 if(m_ShoppingCartList.containsKey(i_ItemName))
 {
 m_ShoppingCartList.remove(i_ItemName);
 return true;
 }
 return false;
 }

 /**
 * Xóa một sản phẩm khỏi giỏ hàng dựa trên đối tượng Item
 * @param i_Item Đối tượng Item cần xóa
 * @return true nếu xóa thành công, false nếu không tìm thấy sản phẩm
 */
 public boolean removeItem(Item i_Item)
 {
 return removeItemByName(i_Item.getName());
 }

 /**
 * Cập nhật số lượng của một sản phẩm trong giỏ hàng
 * @param i_ItemName Tên của sản phẩm cần cập nhật
 * @param i_NewQuantity Số lượng mới cần thiết lập
 * @return true nếu cập nhật thành công, false nếu không tìm thấy sản phẩm
 */
 public boolean updateItemQuantity(String i_ItemName, int i_NewQuantity)
 {
 if(m_ShoppingCartList.containsKey(i_ItemName))
 {
 Item itemToUpdate = m_ShoppingCartList.get(i_ItemName);
 itemToUpdate.setQuantity(i_NewQuantity);
 return true;
 }
 return false;
 }

 /**
 * Tăng số lượng của một sản phẩm trong giỏ hàng lên 1
 * @param i_ItemName Tên của sản phẩm cần tăng số lượng
 * @return true nếu tăng thành công, false nếu không tìm thấy sản phẩm
 */
 public boolean incrementItemQuantity(String i_ItemName)
 {
 if(m_ShoppingCartList.containsKey(i_ItemName))
 {
 Item itemToIncrement = m_ShoppingCartList.get(i_ItemName);
 itemToIncrement.incrementQuantity();
 return true;
 }
 return false;
 }

 /**
 * Giảm số lượng của một sản phẩm trong giỏ hàng xuống 1
 * Nếu số lượng là 1, sản phẩm sẽ được xóa khỏi giỏ hàng
 * @param i_ItemName Tên của sản phẩm cần giảm số lượng
 * @return true nếu giảm thành công, false nếu không tìm thấy sản phẩm
 */
 public boolean decrementItemQuantity(String i_ItemName)
 {
 if(m_ShoppingCartList.containsKey(i_ItemName))
 {
 Item itemToDecrement = m_ShoppingCartList.get(i_ItemName);
 // Nếu số lượng > 1, giảm xuống. Ngược lại, xóa khỏi giỏ hàng
 if(!itemToDecrement.decrementQuantity())
 {
 m_ShoppingCartList.remove(i_ItemName);
 }
 return true;
 }
 return false;
 }

 /**
 * Tính tổng tiền của tất cả các sản phẩm trong giỏ hàng
 * @return Tổng giá trị của giỏ hàng
 */
 public double calculateTotal()
 {
 double total = 0;
 // Duyệt qua tất cả các sản phẩm trong giỏ hàng
 for(Item item : m_ShoppingCartList.values())
 {
 // Cộng dồn giá * số lượng của mỗi sản phẩm
 total += item.getPrice() * item.getQuantity();
 }
 return total;
 }

 /**
 * Kiểm tra xem giỏ hàng có trống không
 * @return true nếu giỏ hàng không có sản phẩm nào, false nếu có ít nhất một sản phẩm
 */
 public boolean isEmpty()
 {
 return m_ShoppingCartList.isEmpty();
 }

 /**
 * Xóa tất cả các sản phẩm trong giỏ hàng
 */
 public void clearCart()
 {
 m_ShoppingCartList.clear();
 }

 /**
 * Lấy số lượng các loại sản phẩm khác nhau trong giỏ hàng
 * @return Số lượng các loại sản phẩm trong giỏ hàng
 */
 public int getItemCount()
 {
 return m_ShoppingCartList.size();
 }

 /**
 * Chuyển đổi giỏ hàng từ HashMap sang List để dễ dàng xử lý hiển thị
 * @return Danh sách các sản phẩm trong giỏ hàng
 */
 public List<Item> getShoppingCartAsList()
 {
 // Tạo một ArrayList mới chứa tất cả các giá trị từ HashMap
 return new ArrayList<>(m_ShoppingCartList.values());
 }

 /**
 * Lấy HashMap chứa tất cả các sản phẩm trong giỏ hàng
 * @return HashMap chứa các sản phẩm với key là tên sản phẩm
 */
 public HashMap<String, Item> getShoppingCart()
 {
 return m_ShoppingCartList;
 }
}
