 // Hiển thị HomeFragment đầu tiên khi khởi động
 m_FragmentManager.beginTransaction().add(R.id.fragmentContainer, m_ShoppingCartFragment, "4").hide(m_ShoppingCartFragment).commit();
 m_FragmentManager.beginTransaction().add(R.id.fragmentContainer, m_LikedItemsFragment, "3").hide(m_LikedItemsFragment).commit();
 m_FragmentManager.beginTransaction().add(R.id.fragmentContainer, m_CategoryFragment, "2").hide(m_CategoryFragment).commit();
 m_FragmentManager.beginTransaction().add(R.id.fragmentContainer, m_HomeFragment, "1").commit();
 m_ActiveFragment = m_HomeFragment;

 // Thiết lập Navigation Drawer
 setupNavigationDrawer();

 // Thiết lập Bottom Navigation
 setupBottomNavigation();

 // Thiết lập nút giỏ hàng trên Toolbar
 setupCartButton();
