 /**
 * Thiết lập Navigation Drawer (menu trượt từ bên trái)
 */
 private void setupNavigationDrawer() {
 m_DrawerLayout = findViewById(R.id.drawerLayout);
 m_NavigationView = findViewById(R.id.navigationView);

 // Tạo ActionBarDrawerToggle để hiển thị biểu tượng hamburger
 ActionBarDrawerToggle toggle = new ActionBarDrawerToggle(
 this, m_DrawerLayout, m_Toolbar, R.string.navigation_drawer_open, R.string.navigation_drawer_close);
 m_DrawerLayout.addDrawerListener(toggle);
 toggle.syncState();

 // Đặt listener cho các item trong Navigation Drawer
 m_NavigationView.setNavigationItemSelectedListener(this);

 // Lấy và thiết lập text đăng nhập/đăng ký trong header của Navigation Drawer
 View headerView = m_NavigationView.getHeaderView(0);
