 startActivity(new Intent(MainActivity.this, LoginPage.class));
 }
 }
 });
 }

 /**
 * Thiết lập Bottom Navigation (thanh điều hướng dưới cùng)
 */
 private void setupBottomNavigation() {
 m_BottomNavigationView = findViewById(R.id.bottomNavigationView);

 // Đặt listener cho các item trong Bottom Navigation
 m_BottomNavigationView.setOnItemSelectedListener(new NavigationBarView.OnItemSelectedListener() {
 @Override
 public boolean onNavigationItemSelected(@NonNull MenuItem item) {
 int itemId = item.getItemId();

 // Chuyển đổi Fragment dựa trên item được chọn
 if (itemId == R.id.action_home) {
 // Chuyển đến Fragment Trang chủ
 m_FragmentManager.beginTransaction().hide(m_ActiveFragment).show(m_HomeFragment).commit();
 m_ActiveFragment = m_HomeFragment;
 return true;
 } else if (itemId == R.id.action_category) {
 // Chuyển đến Fragment Danh mục
 m_FragmentManager.beginTransaction().hide(m_ActiveFragment).show(m_CategoryFragment).commit();
 m_ActiveFragment = m_CategoryFragment;
 return true;
 } else if (itemId == R.id.action_favourites) {
