 @Override
 public void handleOnBackPressed() {
 if (m_DrawerLayout.isDrawerOpen(GravityCompat.START)) {
 // Nếu drawer đang mở, đóng drawer
 m_DrawerLayout.closeDrawer(GravityCompat.START);
 } else if (m_ActiveFragment != m_HomeFragment) {
 // Nếu không ở HomeFragment, quay lại HomeFragment
 m_FragmentManager.beginTransaction().hide(m_ActiveFragment).show(m_HomeFragment).commit();
 m_ActiveFragment = m_HomeFragment;
 m_BottomNavigationView.setSelectedItemId(R.id.action_home);
 } else {
 // Nếu đang ở HomeFragment, hiển thị hộp thoại xác nhận thoát
 showExitConfirmationDialog();
 }
 }
 });
 }

