 /**
 * Cập nhật text hiển thị trên nút đăng nhập/đăng xuất dựa vào trạng thái hiện tại
 */
 private void updateLoginRegisterText() {
 if (m_IsLoggedIn) {
 FirebaseUser user = m_Auth.getCurrentUser();
 if (user != null) {
 String displayName = user.getDisplayName();
 if (displayName != null && !displayName.isEmpty()) {
 m_LoginRegisterTextView.setText(displayName); // Hiển thị tên người dùng nếu có
 } else {
 m_LoginRegisterTextView.setText(user.getEmail()); // Hiển thị email nếu không có tên
 }
 }
 } else {
 m_LoginRegisterTextView.setText(R.string.login_register); // "Login / Register"
 }
 }

