 // Gửi email thất bại, hiển thị thông báo lỗi
 Toast.makeText(ResetPasswordPage.this,
 "Failed to send reset email: " + task.getException().getMessage(),
 Toast.LENGTH_SHORT).show();
 }
 }
 });
 }
