package com.example.argapp.Models;

import android.app.Activity;
import android.util.Log;

import androidx.annotation.NonNull;

import com.example.argapp.Classes.Item;
import com.example.argapp.Classes.ShoppingCart;
import com.example.argapp.Classes.User;
import com.google.android.gms.tasks.OnCompleteListener;
import com.google.android.gms.tasks.Task;
import com.google.firebase.auth.AuthResult;
import com.google.firebase.auth.FirebaseAuth;
import com.google.firebase.auth.FirebaseUser;
import com.google.firebase.database.DataSnapshot;
import com.google.firebase.database.DatabaseError;
import com.google.firebase.database.DatabaseReference;
import com.google.firebase.database.FirebaseDatabase;
import com.google.firebase.database.ValueEventListener;

import java.util.HashMap;
import java.util.Objects;

/**
 * Lớp UserModel xử lý tất cả các tương tác với Firebase Authentication và Realtime Database
 * liên quan đến người dùng, bao gồm xác thực và lưu trữ dữ liệu người dùng
 */
public class UserModel {
 private final String TAG = "UserModel"; // Tag dùng cho các log

 // Các đối tượng Firebase
 private FirebaseAuth m_Auth; // Đối tượng để xử lý xác thực Firebase
 private FirebaseUser m_User; // Người dùng hiện tại đã đăng nhập
 private FirebaseDatabase m_Database; // Cơ sở dữ liệu Firebase
 private DatabaseReference m_Ref; // Tham chiếu đến node trong cơ sở dữ liệu

 /**
 * Constructor khởi tạo UserModel với kết nối đến Firebase
 */
 public UserModel()
 {
 // Khởi tạo đối tượng xác thực Firebase
 m_Auth = FirebaseAuth.getInstance();
 // Lấy thông tin người dùng hiện tại nếu đã đăng nhập
 m_User = m_Auth.getCurrentUser();
 // Khởi tạo đối tượng Firebase Database
 m_Database = FirebaseDatabase.getInstance();
 // Tạo tham chiếu đến node "Users" trong cơ sở dữ liệu
 m_Ref = m_Database.getReference("Users");
 }

 /**
 * Interface callback để xử lý kết quả xác thực (đăng nhập/đăng ký)
 */
 public interface AuthCallback
 {
 void onSuccess(FirebaseUser user); // Gọi khi xác thực thành công
 void onFailure(String errorMessage); // Gọi khi xác thực thất bại
 }

 /**
 * Interface callback để xử lý việc lấy thông tin người dùng
 */
 public interface UserCallback
 {
 void onUserFetched(User user); // Gọi khi lấy thông tin người dùng thành công
 void onFailure(); // Gọi khi không thể lấy thông tin người dùng
 }

 /**
 * Interface callback để xử lý việc cập nhật giỏ hàng
 */
 public interface UpdateShoppingCartCallback
 {
 void onSuccess(); // Gọi khi cập nhật giỏ hàng thành công
 void onFailure(String errorMessage); // Gọi khi cập nhật giỏ hàng thất bại
 }

 /**
 * Interface callback để xử lý việc cập nhật danh sách sản phẩm yêu thích
 */
 public interface UpdateLikedItemsListCallback
 {
 void onSuccess(); // Gọi khi cập nhật danh sách yêu thích thành công
 void onFailure(String errorMessage); // Gọi khi cập nhật danh sách yêu thích thất bại
 }

 /**
 * Interface callback để xử lý việc lấy giỏ hàng
 */
 public interface ShoppingCartCallback
 {
 void onShoppingCartFetched(ShoppingCart shoppingCart); // Gọi khi lấy giỏ hàng thành công
 void onFailure(); // Gọi khi không thể lấy giỏ hàng
 }

 /**
 * Interface callback để xử lý việc lấy danh sách sản phẩm yêu thích
 */
 public interface LikedItemsCallback
 {
