 }

 /**
 * Đăng nhập người dùng với email và mật khẩu
 * @param i_Email Email người dùng
 * @param i_Password Mật khẩu người dùng
 * @param callback Interface callback để phản hồi kết quả
 */
 public void Login(String i_Email, String i_Password, AuthCallback callback)
 {
 // Sử dụng Firebase Authentication để đăng nhập
 m_Auth.signInWithEmailAndPassword(i_Email, i_Password)
 .addOnCompleteListener(new OnCompleteListener<AuthResult>() {
 @Override
 public void onComplete(@NonNull Task<AuthResult> task) {
 if(task.isSuccessful())
 {
 // Nếu đăng nhập thành công, lấy thông tin người dùng
 m_User = m_Auth.getCurrentUser();
 // Gọi callback thành công với thông tin người dùng
 callback.onSuccess(m_User);
 }
 else
 {
 // Nếu đăng nhập thất bại, gọi callback thất bại với thông báo lỗi
 callback.onFailure(Objects.requireNonNull(task.getException()).getMessage());
 }
 }
 });
 }

 /**
 * Đăng ký người dùng mới với thông tin từ đối tượng User
