import java.util.List;

/**
 * Fragment hiển thị danh sách các sản phẩm được người dùng đánh dấu yêu thích
 * Cho phép người dùng xem, quản lý và thao tác với các sản phẩm đã yêu thích
 */
public class LikedItemsFragment extends Fragment implements OnItemListener, OnLikedItemsListUpdatedListener, OnShoppingCartUpdatedListener {

 // Các thành phần giao diện
 private RecyclerView m_LikedItemsRecyclerView; // RecyclerView hiển thị danh sách sản phẩm yêu thích
 private LinearLayout m_EmptyLikedItemsLayout; // Layout hiển thị khi không có sản phẩm yêu thích
 private TextView m_LikedItemsCount; // TextView hiển thị số lượng sản phẩm yêu thích

 // Adapter và dữ liệu
 private LikedItemsAdapter m_LikedItemsAdapter; // Adapter cho RecyclerView
 private List<Item> m_LikedItemsList; // Danh sách các sản phẩm yêu thích
 private HashMap<String, Item> m_LikedItemsMap; // Map lưu trữ sản phẩm yêu thích với key là tên sản phẩm
 private ShoppingCart m_UserShoppingCart; // Giỏ hàng của người dùng

 // Activity chứa fragment
 private MainActivity m_HostedActivity; // Activity chính chứa Fragment này
 private View m_View; // View chính của fragment

 /**
 * Constructor rỗng bắt buộc cho Fragment
 * Android yêu cầu mọi Fragment phải có constructor không tham số
 */
 public LikedItemsFragment() {
 // Required empty public constructor
 }

 /**
 * Được gọi khi fragment được tạo
 * Khởi tạo các biến thành viên không phải giao diện
 */
 @Override
 public void onCreate(Bundle savedInstanceState) {
 super.onCreate(savedInstanceState);
 }

 /**
 * Được gọi để tạo giao diện người dùng của fragment
 * Inflate layout và thiết lập các thành phần UI
 */
 @Override
 public View onCreateView(LayoutInflater inflater, ViewGroup container,
 Bundle savedInstanceState) {
 // Inflate layout cho fragment
 m_View = inflater.inflate(R.layout.fragment_liked_items, container, false);

 // Lấy tham chiếu đến Activity chứa Fragment
 m_HostedActivity = (MainActivity) requireActivity();

 // Khởi tạo các thành phần giao diện và dữ liệu
 initialize();

 // Hiển thị danh sách sản phẩm yêu thích
 displayLikedItems();

 return m_View;
 }

 /**
 * Được gọi khi fragment trở nên hiển thị với người dùng
 * Đăng ký các listener để theo dõi sự thay đổi dữ liệu
 */
 @Override
 public void onResume() {
 super.onResume();

 // Đăng ký fragment này để lắng nghe sự kiện cập nhật giỏ hàng
 m_HostedActivity.SetShoppingCartUpdatedListener(this);

 // Đăng ký fragment này để lắng nghe sự kiện cập nhật danh sách yêu thích
 m_HostedActivity.SetLikedItemsListUpdateListener(this);
 }

 /**
 * Khởi tạo các thành phần giao diện và dữ liệu
 */
 private void initialize() {
 // Ánh xạ các thành phần giao diện từ layout
 m_LikedItemsRecyclerView = m_View.findViewById(R.id.likedItemsRecyclerView);
 m_EmptyLikedItemsLayout = m_View.findViewById(R.id.emptyLikedItemsLayout);
 m_LikedItemsCount = m_View.findViewById(R.id.likedItemsCount);

 // Thiết lập LayoutManager cho RecyclerView
 m_LikedItemsRecyclerView.setLayoutManager(new LinearLayoutManager(getContext()));

 // Lấy giỏ hàng và danh sách sản phẩm yêu thích từ MainActivity
 m_UserShoppingCart = m_HostedActivity.GetUserShoppingCart();
 m_LikedItemsMap = m_HostedActivity.GetUserLikedItemsList();

 // Khởi tạo danh sách các sản phẩm yêu thích từ HashMap
 m_LikedItemsList = new ArrayList<>(m_LikedItemsMap.values());
 }

 /**
 * Hiển thị danh sách sản phẩm yêu thích
 * Hoặc hiển thị thông báo nếu không có sản phẩm yêu thích
 */
 private void displayLikedItems() {
 // Cập nhật số lượng sản phẩm yêu thích hiển thị trên UI
 m_LikedItemsCount.setText("(" + m_LikedItemsList.size() + ")");

 // Kiểm tra xem có sản phẩm yêu thích nào không
 if (m_LikedItemsList.isEmpty()) {
 // Nếu không có, hiển thị layout trống
 m_EmptyLikedItemsLayout.setVisibility(View.VISIBLE);
 m_LikedItemsRecyclerView.setVisibility(View.GONE);
 } else {
 // Nếu có, hiển thị danh sách sản phẩm
 m_EmptyLikedItemsLayout.setVisibility(View.GONE);
 m_LikedItemsRecyclerView.setVisibility(View.VISIBLE);

 // Tạo adapter mới cho RecyclerView với danh sách sản phẩm yêu thích
 m_LikedItemsAdapter = new LikedItemsAdapter(m_LikedItemsList, m_HostedActivity, this);
 m_LikedItemsRecyclerView.setAdapter(m_LikedItemsAdapter);
 }
 }

 /**
 * Được gọi khi người dùng click vào một sản phẩm trong danh sách
 * @param item Đối tượng Item được click
 */
 @Override
 public void onItemClick(Item item) {
 // Tạo Bundle để truyền dữ liệu sang EditItem Fragment
 Bundle args = new Bundle();
 args.putSerializable("item", item);

 // Tạo một instance của EditItem Fragment
 EditItem editItemFragment = new EditItem();
 editItemFragment.setArguments(args);

 // Thêm EditItem Fragment vào back stack và hiển thị nó
 requireActivity().getSupportFragmentManager().beginTransaction()
 .replace(R.id.fragmentContainer, editItemFragment)
 .addToBackStack(null)
 .commit();
 }

 /**
 * Được gọi khi người dùng click vào nút "Thêm vào giỏ hàng"
 * @param item Đối tượng Item cần thêm vào giỏ hàng
 */
 @Override
 public void onAddToCartClick(Item item) {
 // Thêm sản phẩm vào giỏ hàng
 m_UserShoppingCart.addItem(item);

 // Cập nhật giỏ hàng trong MainActivity
 m_HostedActivity.UpdateShoppingCart(m_UserShoppingCart);
 }

 /**
 * Được gọi khi người dùng click vào nút "Yêu thích"
 * @param item Đối tượng Item cần thêm/xóa khỏi danh sách yêu thích
 */
 @Override
 public void onLikeClick(Item item) {
 // Kiểm tra xem sản phẩm có trong danh sách yêu thích không
 if (m_LikedItemsMap.containsKey(item.getName())) {
 // Nếu có, xóa khỏi danh sách yêu thích
 m_LikedItemsMap.remove(item.getName());
 } else {
 // Nếu không, thêm vào danh sách yêu thích
 m_LikedItemsMap.put(item.getName(), item);
 }

 // Cập nhật danh sách sản phẩm yêu thích
 m_LikedItemsList = new ArrayList<>(m_LikedItemsMap.values());

 // Cập nhật danh sách yêu thích trong MainActivity
 m_HostedActivity.UpdateLikedItemsList(m_LikedItemsMap);

 // Cập nhật giao diện để hiển thị thay đổi
 displayLikedItems();
 }

 /**
 * Được gọi khi danh sách sản phẩm yêu thích được cập nhật
 * Triển khai từ interface OnLikedItemsListUpdatedListener
 */
 @Override
 public void OnLikedItemsListUpdated() {
 // Lấy danh sách yêu thích mới từ MainActivity
 m_LikedItemsMap = m_HostedActivity.GetUserLikedItemsList();

 // Cập nhật danh sách sản phẩm yêu thích
 m_LikedItemsList = new ArrayList<>(m_LikedItemsMap.values());

 // Cập nhật giao diện để hiển thị thay đổi
 displayLikedItems();
 }

 /**
 * Được gọi khi giỏ hàng được cập nhật
 * Triển khai từ interface OnShoppingCartUpdatedListener
 */
 @Override
 public void OnShoppingCartUpdated() {
 // Lấy giỏ hàng mới từ MainActivity
 m_UserShoppingCart = m_HostedActivity.GetUserShoppingCart();

 // Nếu adapter đã được khởi tạo, thông báo cho nó cập nhật dữ liệu
 if (m_LikedItemsAdapter != null) {
 m_LikedItemsAdapter.notifyDataSetChanged();
 }
 }
}
