 /**
 * Hiển thị giỏ hàng hoặc thông báo giỏ hàng trống
 */
 private void displayCart() {
 // Lấy danh sách sản phẩm từ giỏ hàng
 m_ShoppingCartAsList = m_UserShoppingCart.getShoppingCartAsList();

 // Cập nhật tổng giá tiền hiển thị
 m_TotalPrice.setText(String.format("%.2f", m_UserShoppingCart.calculateTotal()));

 // Kiểm tra xem giỏ hàng có trống không
