 m_EmptyCartLayout.setVisibility(View.VISIBLE);
 m_ShoppingCartRecyclerView.setVisibility(View.GONE);
 m_CheckoutButton.setEnabled(false); // Vô hiệu hóa nút thanh toán
 m_ClearCartButton.setEnabled(false); // Vô hiệu hóa nút xóa giỏ hàng
 } else {
 // Nếu giỏ hàng có sản phẩm, hiển thị danh sách sản phẩm
 m_EmptyCartLayout.setVisibility(View.GONE);
 m_ShoppingCartRecyclerView.setVisibility(View.VISIBLE);
 m_CheckoutButton.setEnabled(true); // Kích hoạt nút thanh toán
 m_ClearCartButton.setEnabled(true); // Kích hoạt nút xóa giỏ hàng

 // Tạo adapter mới cho RecyclerView với danh sách sản phẩm trong giỏ hàng
 m_ShoppingCartAdapter = new ShoppingCartAdapter(m_ShoppingCartAsList, m_HostedActivity, this);
 m_ShoppingCartRecyclerView.setAdapter(m_ShoppingCartAdapter);
 }
