 {
 m_BottomNavigationView.removeBadge(R.id.shoppingCartBarButton);
 }

 // Kích hoạt nút giỏ hàng
 m_BottomNavigationView.getMenu().findItem(R.id.shoppingCartBarButton).setEnabled(true);
 }

