 }else
 {
 m_BottomNavigationView.removeBadge(R.id.likedItemsBarButton);
 }

 // Kích hoạt nút danh sách yêu thích
 m_BottomNavigationView.getMenu().findItem(R.id.likedItemsBarButton).setEnabled(true);
 }
