## 6. Cập nhật OrderBillAdapter.java

```java:app/src/main/java/com/example/argapp/Adapters/OrderBillAdapter.java
package com.example.argapp.Adapters;

import android.content.Context;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.example.argapp.Classes.OrderBill;
import com.example.argapp.R;

import java.text.SimpleDateFormat;
import java.util.Date;
import java.util.List;
import java.util.Locale;

public class OrderBillAdapter extends RecyclerView.Adapter<OrderBillAdapter.OrderBillViewHolder> {

 private List<OrderBill> orderBillList;
 private Context context;
 private OnOrderBillClickListener listener;

 public interface OnOrderBillClickListener {
 void onOrderBillClick(OrderBill orderBill);
 }

 public OrderBillAdapter(List<OrderBill> orderBillList, Context context, OnOrderBillClickListener listener) {
 this.orderBillList = orderBillList;
 this.context = context;
 this.listener = listener;
 }

 public static class OrderBillViewHolder extends RecyclerView.ViewHolder {
 TextView tvOrderId, tvOrderDate, tvTotalItems, tvTotalPrice;
 Button btnStatus;

 public OrderBillViewHolder(@NonNull View itemView) {
 super(itemView);
 tvOrderId = itemView.findViewById(R.id.tvOrderId);
 tvOrderDate = itemView.findViewById(R.id.tvOrderDate);
 tvTotalItems = itemView.findViewById(R.id.tvTotalItems);
 tvTotalPrice = itemView.findViewById(R.id.tvPrice);
 btnStatus = itemView.findViewById(R.id.btnStatus);
 }
 }

 @NonNull
 @Override
 public OrderBillViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
 View view = LayoutInflater.from(context).inflate(R.layout.fragment_order_history_item, parent, false);
 return new OrderBillViewHolder(view);
 }

 @Override
 public void onBindViewHolder(@NonNull OrderBillViewHolder holder, int position) {
 OrderBill orderBill = orderBillList.get(position);
 if (orderBill == null)
 return;

 // Format the Order ID (first 8 characters)
 String orderId = orderBill.getOrderBillId();
 if (orderId != null && orderId.length() > 8) {
 orderId = orderId.substring(0, 8) + "...";
 }
 holder.tvOrderId.setText("Order #" + orderId);

 // Format date
 SimpleDateFormat sdf = new SimpleDateFormat("dd/MM/yyyy HH:mm", Locale.getDefault());
 String formattedDate = sdf.format(new Date(orderBill.getOrderDate()));
 holder.tvOrderDate.setText(formattedDate);

 // Calculate total items
 int totalItems = 0;
 if (orderBill.getItems() != null) {
 for (OrderBillItem item : orderBill.getItems().values()) {
 totalItems += item.getQuantity();
 }
 }
 holder.tvTotalItems.setText(totalItems + " items");

 // Format price
 holder.tvTotalPrice.setText(String.format("$%.2f", orderBill.getTotalPrice()));

 // Set status button
 holder.btnStatus.setText(orderBill.getStatus());

 // Style the status button based on status
 switch (orderBill.getStatus().toUpperCase()) {
 case "PENDING":
 holder.btnStatus.setBackgroundResource(R.drawable.status_pending_background);
 break;
 case "PROCESSING":
 holder.btnStatus.setBackgroundResource(R.drawable.status_processing_background);
 break;
 case "SHIPPED":
 holder.btnStatus.setBackgroundResource(R.drawable.status_shipped_background);
 break;
 case "DELIVERED":
 holder.btnStatus.setBackgroundResource(R.drawable.status_delivered_background);
 break;
 case "CANCELLED":
 holder.btnStatus.setBackgroundResource(R.drawable.status_cancelled_background);
 break;
 default:
 holder.btnStatus.setBackgroundResource(R.drawable.status_default_background);
 break;
 }

 // Set click listener
 holder.itemView.setOnClickListener(v -> {
 if (listener != null) {
 listener.onOrderBillClick(orderBill);
 }
 });
 }

 @Override
 public int getItemCount() {
 return this.orderBillList != null ? this.orderBillList.size() : 0;
 }

 // Thêm phương thức để cập nhật dữ liệu
 public void setOrderBills(List<OrderBill> orderBills) {
 this.orderBillList = orderBills;
 notifyDataSetChanged();
 }
}
```

