import android.widget.ImageView;
import android.widget.ProgressBar;
import android.widget.TextView;
import android.widget.Toast;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.navigation.Navigation;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.argapp.Adapters.OrderItemAdapter;
import com.example.argapp.Classes.OrderBill;
import com.example.argapp.Classes.OrderBillItem;
import com.example.argapp.Controllers.UserController;
import com.example.argapp.Models.UserModel;
import com.example.argapp.R;

import java.text.SimpleDateFormat;
import java.util.ArrayList;
import java.util.Date;
import java.util.List;
import java.util.Locale;
import java.util.Map;

public class OrderDetailFragment extends Fragment {

 private TextView tvOrderId, tvOrderDate, tvOrderStatus, tvTotalPrice, tvAddress, tvPhoneNumber;
 private RecyclerView rvOrderItems;
 private ProgressBar progressBar;
 private Button btnTrackOrder;
 private UserController userController;
 private String orderBillId;
 private View view;

 @Override
 public void onCreate(@Nullable Bundle savedInstanceState) {
 super.onCreate(savedInstanceState);
 userController = new UserController();
 }

 @Nullable
 @Override
 public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
 view = inflater.inflate(R.layout.fragment_order_detail, container, false);

 // Khởi tạo views
 tvOrderId = view.findViewById(R.id.tvOrderId);
 tvOrderDate = view.findViewById(R.id.tvOrderDate);
 tvOrderStatus = view.findViewById(R.id.tvOrderStatus);
 tvTotalPrice = view.findViewById(R.id.tvTotalPrice);
 tvAddress = view.findViewById(R.id.tvAddress);
 tvPhoneNumber = view.findViewById(R.id.tvPhoneNumber);
 rvOrderItems = view.findViewById(R.id.rvOrderItems);
 progressBar = view.findViewById(R.id.progressBar);
 btnTrackOrder = view.findViewById(R.id.btnTrackOrder);

 // Thiết lập RecyclerView
 rvOrderItems.setLayoutManager(new LinearLayoutManager(getContext()));

 // Thiết lập sự kiện click cho nút back
 ImageView btnBack = view.findViewById(R.id.btnBack);
 btnBack.setOnClickListener(v -> Navigation.findNavController(v).popBackStack());

 // Lấy orderBillId từ arguments
 if (getArguments() != null && getArguments().containsKey("orderBillId")) {
 orderBillId = getArguments().getString("orderBillId");
 loadOrderDetail(orderBillId);
 } else {
 Toast.makeText(getContext(), "Không tìm thấy thông tin đơn hàng", Toast.LENGTH_SHORT).show();
 Navigation.findNavController(view).popBackStack();
 }

 return view;
 }

 private void loadOrderDetail(String orderBillId) {
 // Hiển thị ProgressBar
 progressBar.setVisibility(View.VISIBLE);

 // Lấy chi tiết đơn hàng từ Firebase
 userController.getOrderBillDetails(orderBillId, new UserModel.OrderBillDetailsCallback() {
 @Override
 public void onSuccess(OrderBill orderBill) {
 // Ẩn ProgressBar
 progressBar.setVisibility(View.GONE);

 if (orderBill != null) {
 // Hiển thị thông tin đơn hàng
 displayOrderInfo(orderBill);
 } else {
 Toast.makeText(getContext(), "Không tìm thấy thông tin đơn hàng", Toast.LENGTH_SHORT).show();
 Navigation.findNavController(view).popBackStack();
 }
 }

 @Override
 public void onFailure(DatabaseError error) {
 // Ẩn ProgressBar
 progressBar.setVisibility(View.GONE);

 // Hiển thị thông báo lỗi
 Toast.makeText(getContext(), "Lỗi khi tải chi tiết đơn hàng: " + error.getMessage(), Toast.LENGTH_SHORT).show();
 Navigation.findNavController(view).popBackStack();
 }
 });
 }

 private void displayOrderInfo(OrderBill orderBill) {
 // Hiển thị ID đơn hàng
 tvOrderId.setText("Mã đơn hàng: #" + orderBill.getOrderBillId());

 // Hiển thị ngày đặt hàng
 SimpleDateFormat sdf = new SimpleDateFormat("dd/MM/yyyy HH:mm", Locale.getDefault());
 tvOrderDate.setText("Ngày đặt: " + sdf.format(new Date(orderBill.getOrderDate())));

 // Hiển thị trạng thái đơn hàng
 tvOrderStatus.setText("Trạng thái: " + orderBill.getStatus());
 styleOrderStatus(orderBill.getStatus());

 // Hiển thị tổng giá
 tvTotalPrice.setText(String.format(Locale.getDefault(), "Tổng tiền: %.2f VNĐ", orderBill.getTotalPrice()));

 // Hiển thị địa chỉ giao hàng và số điện thoại
 tvAddress.setText("Địa chỉ: " + (orderBill.getAddress() != null ? orderBill.getAddress() : "Chưa cập nhật"));
 tvPhoneNumber.setText("Điện thoại: " + (orderBill.getPhoneNumber() != null ? orderBill.getPhoneNumber() : "Chưa cập nhật"));

 // Thiết lập nút theo dõi đơn hàng
 if ("DELIVERED".equalsIgnoreCase(orderBill.getStatus()) || "CANCELLED".equalsIgnoreCase(orderBill.getStatus())) {
 btnTrackOrder.setVisibility(View.GONE);
 } else {
 btnTrackOrder.setVisibility(View.VISIBLE);
 btnTrackOrder.setOnClickListener(v -> {
 // TODO: Tính năng theo dõi đơn hàng (có thể hiển thị dialog hoặc chuyển đến màn hình theo dõi)
 Toast.makeText(getContext(), "Tính năng theo dõi đơn hàng đang phát triển", Toast.LENGTH_SHORT).show();
 });
 }

 // Hiển thị danh sách sản phẩm
 if (orderBill.getItems() != null && !orderBill.getItems().isEmpty()) {
 List<OrderBillItem> items = new ArrayList<>(orderBill.getItems().values());
 OrderItemAdapter adapter = new OrderItemAdapter(items, getContext());
 rvOrderItems.setAdapter(adapter);
 }
 }

 private void styleOrderStatus(String status) {
 int backgroundColor;
 int textColor = getResources().getColor(android.R.color.white);

 switch (status.toUpperCase()) {
 case "PENDING":
 backgroundColor = getResources().getColor(R.color.colorStatusPending);
 break;
 case "PROCESSING":
 backgroundColor = getResources().getColor(R.color.colorStatusProcessing);
 break;
 case "SHIPPED":
 backgroundColor = getResources().getColor(R.color.colorStatusShipped);
 break;
 case "DELIVERED":
 backgroundColor = getResources().getColor(R.color.colorStatusDelivered);
 break;
 case "CANCELLED":
 backgroundColor = getResources().getColor(R.color.colorStatusCancelled);
 break;
 default:
 backgroundColor = getResources().getColor(R.color.colorStatusDefault);
 break;
 }

 tvOrderStatus.setBackgroundColor(backgroundColor);
 tvOrderStatus.setTextColor(textColor);
 }
}
```

## 11. Tạo Adapter cho các item trong đơn hàng - OrderItemAdapter.java

```java:app/src/main/java/com/example/argapp/Adapters/OrderItemAdapter.java
package com.example.argapp.Adapters;

import android.content.Context;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.example.argapp.Classes.OrderBillItem;
import com.example.argapp.R;

import java.util.List;
import java.util.Locale;

public class OrderItemAdapter extends RecyclerView.Adapter<OrderItemAdapter.OrderItemViewHolder> {

 private List<OrderBillItem> items;
 private Context context;

 public OrderItemAdapter(List<OrderBillItem> items, Context context) {
 this.items = items;
 this.context = context;
 }

 @NonNull
 @Override
 public OrderItemViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
 View view = LayoutInflater.from(context).inflate(R.layout.item_order_detail, parent, false);
 return new OrderItemViewHolder(view);
 }

 @Override
 public void onBindViewHolder(@NonNull OrderItemViewHolder holder, int position) {
 OrderBillItem item = items.get(position);

 // Hiển thị tên sản phẩm
 holder.tvProductName.setText(item.getProductName());

 // Hiển thị số lượng và đơn vị
 holder.tvQuantity.setText(String.format(Locale.getDefault(), "%d %s", 
 item.getQuantity(), item.getUnit()));

 // Hiển thị giá
 holder.tvPrice.setText(String.format(Locale.getDefault(), "%.2f VNĐ", 
 item.getSalePrice() * item.getQuantity()));

 // Tải hình ảnh sản phẩm
 String imageName = item.getImage();
 if (imageName != null && imageName.contains("/")) {
 // Trích xuất tên hình ảnh từ đường dẫn drawable
 imageName = imageName.substring(imageName.lastIndexOf("/") + 1);
 }

 int resourceId = context.getResources().getIdentifier(
 imageName, "drawable", context.getPackageName());

 if (resourceId != 0) {
 holder.ivProductImage.setImageResource(resourceId);
 } else {
 holder.ivProductImage.setImageResource(R.drawable.ic_default_product);
 }
 }

 @Override
 public int getItemCount() {
 return items != null ? items.size() : 0;
 }

 public static class OrderItemViewHolder extends RecyclerView.ViewHolder {
 ImageView ivProductImage;
 TextView tvProductName, tvQuantity, tvPrice;

 public OrderItemViewHolder(@NonNull View itemView) {
 super(itemView);
 ivProductImage = itemView.findViewById(R.id.ivProductImage);
 tvProductName = itemView.findViewById(R.id.tvProductName);
 tvQuantity = itemView.findViewById(R.id.tvQuantity);
 tvPrice = itemView.findViewById(R.id.tvPrice);
 }
 }
}
```

