// Thêm phương thức để hiển thị hộp thoại xác nhận đăng xuất
private void showLogoutConfirmationDialog() {
 androidx.appcompat.app.AlertDialog.Builder builder = new androidx.appcompat.app.AlertDialog.Builder(requireContext());
 builder.setTitle("Xác nhận đăng xuất");
 builder.setMessage("Bạn có chắc chắn muốn đăng xuất?");

 builder.setPositiveButton("Xác nhận", (dialog, which) -> {
 // Đăng xuất khỏi hệ thống
 performLogout();
 });

 builder.setNegativeButton("Hủy", (dialog, which) -> {
 dialog.dismiss();
 });

 builder.create().show();
}

// Phương thức để thực hiện đăng xuất
private void performLogout() {
 // Đăng xuất khỏi Firebase Auth
 com.google.firebase.auth.FirebaseAuth.getInstance().signOut();

 // Chuyển hướng đến trang login
 NavController navController = Navigation.findNavController(requireActivity(), R.id.nav_host_fragment);
 navController.navigate(R.id.action_profilePage_to_loginActivity);

 // Có thể cần thêm các bước xóa dữ liệu người dùng lưu trữ trong ứng dụng
 // Ví dụ: xóa SharedPreferences, v.v.

 // Thông báo đăng xuất thành công
 Toast.makeText(requireContext(), "Đăng xuất thành công", Toast.LENGTH_SHORT).show();
}
