 int shoppingCartQuantity = m_UserShoppingCart.getShoppingCart().size();

 // Tạo hoặc lấy badge cho nút giỏ hàng
 m_BadgeDrawable = m_BottomNavigationView.getOrCreateBadge(R.id.shoppingCartBarButton);

